import React from "react";
import {
    Container,
    Navbar,
    Row,
    Col,
    Button,
    Modal,
    Form,
} from "react-bootstrap";
import "./HomePage.css";
import { useState } from "react";

const HomePage = () => {
    const [showLoginForm, setShowLoginForm] = useState(false);

    const ModalLogin = () => {
        return (
            <Modal show={showLoginForm} onHide={() => setShowLoginForm(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>Đăng nhập</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Row className="my-2">
                            <Col>
                                <Form.Label>Số điện thoại</Form.Label>
                                <Form.Control
                                    type="text"
                                    placeholder="Nhập số điện thoại"
                                    // value={teamData.teamName}
                                    // onChange={(e) =>
                                    //     setTeamData({
                                    //         ...teamData,
                                    //         teamName: e.target.value,
                                    //     })
                                    // }
                                />
                            </Col>
                        </Row>
                        <Row className="my-2">
                            <Col>
                                <Form.Label>Mật khẩu</Form.Label>
                                <Form.Control
                                    type="password"
                                    placeholder="Nhập mật khẩu"
                                    // onChange={(e) =>
                                    //     setTeamData({
                                    //         ...teamData,
                                    //         teamName: e.target.value,
                                    //     })
                                    // }
                                />
                            </Col>
                        </Row>
                        <Row className="my-2">
                            <Col className="text-center">
                                <Button
                                    variant="danger"
                                    onClick={() => setShowLoginForm(true)}
                                >
                                    Đăng nhập
                                </Button>
                            </Col>
                        </Row>
                        <hr />
                        <Row>
                            <Col>
                                <Form.Label>Đội 1</Form.Label>
                                <Form.Control
                                    type="text"
                                    placeholder="Đội 1"
                                    // onChange={(e) =>
                                    //     setTeamData({
                                    //         ...teamData,
                                    //         teamName: e.target.value,
                                    //     })
                                    // }
                                />
                            </Col>
                            <Col>
                                <Form.Label>Đội 2</Form.Label>
                                <Form.Control
                                    type="text"
                                    placeholder="Đội 2"
                                    // onChange={(e) =>
                                    //     setTeamData({
                                    //         ...teamData,
                                    //         teamName: e.target.value,
                                    //     })
                                    // }
                                />
                            </Col>
                        </Row>
                        <Row>
                            <Col>
                                <Form.Label>Tỷ số</Form.Label>
                                <Form.Control
                                    type="text"
                                    placeholder="Tỷ số"
                                    // onChange={(e) =>
                                    //     setTeamData({
                                    //         ...teamData,
                                    //         teamName: e.target.value,
                                    //     })
                                    // }
                                />
                            </Col>
                        </Row>
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button
                        variant="secondary"
                        onClick={() => setShowLoginForm(false)}
                    >
                        Save
                    </Button>
                    <Button
                        variant="danger"
                        onClick={() => setShowLoginForm(false)}
                    >
                        Cancel
                    </Button>
                </Modal.Footer>
            </Modal>
        );
    };

    return (
        <Container fluid>
            <div className="home-page"></div>
            <Navbar>
                <Container fluid>
                    <Navbar.Brand href="#home" className="text-white">
                        Soccer Tour
                    </Navbar.Brand>
                    <Navbar.Toggle />
                    <Navbar.Collapse className="justify-content-end">
                        <Button
                            variant="danger"
                            className="mx-3"
                            onClick={() => setShowLoginForm(true)}
                        >
                            Đăng nhập
                        </Button>
                        <Button variant="danger">Đăng xuất</Button>
                    </Navbar.Collapse>
                </Container>
            </Navbar>
            <ModalLogin />
            <Container
                fluid
                className="text-center text-white"
                style={{
                    position: "relative",
                }}
            >
                <h1 className="my-5">Simple Approach to Manage Tournaments!</h1>
                <p className="my-1">
                    FootballTour.com to simplify tournament management process
                    in order to save your time
                </p>
                <p className="my-1">Join us now and you will love this.</p>
                <p className="my-5">
                    FootballTour tự tin đáp ứng được những nhu cầu thể thao mà
                    người quản lí đòi hỏi với chất lượng tuyệt vời nhất. Khán
                    giả yêu thích bóng đá hoàn toàn có thể thỏa mãn đam mê của
                    mình nhờ hệ thống tra cứu và quản lí bóng đá mà chúng tôi
                    cung cấp. Chắc chắn các bạn sẽ cảm thấy hài lòng với những
                    dịch vụ được cung cấp.
                </p>

                <Row>
                    <Col>
                        <h3>1.509</h3>
                        <p>GIẢI ĐẤU</p>
                    </Col>
                    <Col>
                        <h3>40.320</h3>
                        <p>ĐỘI</p>
                    </Col>
                    <Col>
                        <h3>1.052.202</h3>
                        <p>LƯỢT XEM</p>
                    </Col>
                </Row>
            </Container>
        </Container>
    );
};

export default HomePage;
