import React, { Component } from "react";
import { Container, Button, Row, Col, Modal, Form } from "react-bootstrap";

export default class MatchRow extends Component {
    render() {
        return (
            <Row>
                <Modal
                    show={showMatchInfo}
                    onHide={() => setShowMatchInfo(false)}
                    className="w-100"
                >
                    <Modal.Header closeButton>
                        <Modal.Title>Cập nhật thông tin trận đấu</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <Form>
                            <Row>
                                <Col>
                                    <Form.Label>Ngày</Form.Label>
                                    <Form.Control
                                        type="date"
                                        placeholder="Nhập tên đội"
                                        // value={teamData.teamName}
                                        // onChange={(e) =>
                                        //     setTeamData({
                                        //         ...teamData,
                                        //         teamName: e.target.value,
                                        //     })
                                        // }
                                    />
                                </Col>
                                <Col>
                                    <Form.Label>Giờ</Form.Label>
                                    <Form.Control
                                        type="time"
                                        placeholder="Nhập tên đội"
                                        // value={teamData.teamName}
                                        // onChange={(e) =>
                                        //     setTeamData({
                                        //         ...teamData,
                                        //         teamName: e.target.value,
                                        //     })
                                        // }
                                    />
                                </Col>
                            </Row>
                            <Row>
                                <Col>
                                    <Form.Label>Sân</Form.Label>
                                    <Form.Control
                                        type="text"
                                        placeholder="Nhập tên đội"
                                        value="Sân"
                                        // onChange={(e) =>
                                        //     setTeamData({
                                        //         ...teamData,
                                        //         teamName: e.target.value,
                                        //     })
                                        // }
                                    />
                                </Col>
                            </Row>
                            <hr />
                            <Row>
                                <Col>
                                    <Form.Label>Đội 1</Form.Label>
                                    <Form.Control
                                        type="text"
                                        placeholder="Đội 1"
                                        value={team1}
                                        // onChange={(e) =>
                                        //     setTeamData({
                                        //         ...teamData,
                                        //         teamName: e.target.value,
                                        //     })
                                        // }
                                    />
                                </Col>
                                <Col>
                                    <Form.Label>Đội 2</Form.Label>
                                    <Form.Control
                                        type="text"
                                        placeholder="Đội 2"
                                        value={team2}
                                        // onChange={(e) =>
                                        //     setTeamData({
                                        //         ...teamData,
                                        //         teamName: e.target.value,
                                        //     })
                                        // }
                                    />
                                </Col>
                            </Row>
                            <Row>
                                <Col>
                                    <Form.Label>Tỷ số</Form.Label>
                                    <Form.Control
                                        type="text"
                                        placeholder="Tỷ số"
                                        // onChange={(e) =>
                                        //     setTeamData({
                                        //         ...teamData,
                                        //         teamName: e.target.value,
                                        //     })
                                        // }
                                    />
                                </Col>
                            </Row>
                        </Form>
                    </Modal.Body>
                    <Modal.Footer>
                        <Button
                            variant="secondary"
                            onClick={() => setShowMatchInfo(false)}
                        >
                            Save
                        </Button>
                        <Button
                            variant="danger"
                            onClick={() => setShowMatchInfo(false)}
                        >
                            Cancel
                        </Button>
                    </Modal.Footer>
                </Modal>
                <Col sm={1} className="text-center">
                    <p>Ngày</p>
                </Col>
                <Col sm={1} className="text-center">
                    <p>Giờ</p>
                </Col>
                <Col sm={4} className="text-center">
                    <p>{this.props.team1}</p>
                </Col>
                <Col sm={4} className="text-center">
                    <p>{this.props.team1}</p>
                </Col>
                <Col sm={2} className="text-center align-center">
                    <Button
                        variant="primary"
                        type="button"
                        onClick={updateMatch}
                    >
                        Cập nhật Thông tin
                    </Button>
                </Col>
            </Row>
        );
    }
}
