import {
    FETCH_ALL_TOUR,
    UPDATE_CALENDAR,
    UPDATE_MATCH,
    UPDATE_MATCH_RESULT,
    CREATE_TOUR,
    CREATE_TEAM,
    FETCH_ALL_PLAYER
} from "../constants/actionTypes.js";

export default (tour = {}, action) => {
    console.log(action.type);
    switch (action.type) {
        case CREATE_TEAM:
            tour.allTeams.push(action.payload);
            tour.players.push(...action.payload.playerList)
            console.log("Saved team in reducer");
            return tour;
        case CREATE_TOUR:
            console.log("Saved tour in reducer");
            return action.payload;
        case FETCH_ALL_TOUR:
            return action.payload;
        case UPDATE_CALENDAR:
            console.log("Updated calendar in tour");
            return action.payload;
        case UPDATE_MATCH:
            if (tour) {
                for (let homeOrAway of ["awayMatches", "homeMatches"]) {
                    for (const round in tour.calendar[homeOrAway]) {
                        for (const match in tour.calendar[homeOrAway][round]
                            .matches) {
                            if (
                                tour.calendar[homeOrAway][round].matches[
                                    match
                                ]._id === action.payload._id
                            ) {
                                tour.calendar[homeOrAway][round].matches[
                                    match
                                ] = action.payload;
                                console.log("Updated Data in reducer");
                                return tour;
                            }
                        }
                    }
                }
            }
            return tour;
        case UPDATE_MATCH_RESULT:
            if (Object.keys(tour).length !== 0) {
                for (let homeOrAway of ["awayMatches", "homeMatches"]) {
                    for (const round in tour.calendar[homeOrAway]) {
                        for (const match in tour.calendar[homeOrAway][round]
                            .matches) {
                            if (
                                tour.calendar[homeOrAway][round].matches[
                                    match
                                ].result._id === action.payload._id
                            ) {
                                tour.calendar[homeOrAway][round].matches[
                                    match
                                ].result = action.payload;
                                console.log("Updated result in reducer");
                                return tour;
                            }
                        }
                    }
                }
            }
            return tour;
        case FETCH_ALL_PLAYER: 
            tour.players = action.payload
            return tour;
        default:
            return tour;
    }
};
