import _ from "lodash";
import React, { useState } from "react";
import { Container, Button, Row, Col, Modal, Form } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import { updateCalendar } from "../actions/calendar.js";
import { updateMatch, updateMatchResult } from "../actions/match.js";
import MatchRow from "./MatchRow.js";

const initializeMatchData = {
    team1: {
        teamName: "",
        homeGround: "",
        playerList: [],
    },
    team2: {
        teamName: "",
        homeGround: "",
        playerList: [],
    },
    field: "",
    vongthidau: null,
    tenluotthidau: "",
    date: null,
    time: null,
    result: null,
    _id: null,
};
const initializeMatchResult = {
    _id: null,
    isConfirm: false,
    team1Result: {
        totalGoals: null,
        goals: [
            {
                name: "",
                player: "",
                assist: "",
                type: "",
                minute: "",
                second: "",
            },
        ],
    },
    team2Result: {
        totalGoals: null,
        goals: [
            {
                name: "",
                player: "",
                assist: "",
                type: "",
                minute: "",
                second: "",
            },
        ],
    },
};

function Calendar() {
    const dispatch = useDispatch();
    const calendar = useSelector((state) => state.calendar);
    const [showMatchInfo, setShowMatchInfo] = useState(false);
    const [selectedMatch, setSelectedMatch] = useState(initializeMatchData);
    const [showMatchResult, setShowMatchResult] = useState(false);
    const [selectedMatchResult, setSelectedMatchResult] = useState(
        initializeMatchResult
    );

    const onSaveMatchInfo = async () => {
        await dispatch(updateMatch(selectedMatch._id, selectedMatch));
        setShowMatchInfo(false);
    };

    const onSaveMatchResult = async () => {
        const temp = _.cloneDeep(selectedMatchResult);
        // Change player index to player id 
        for (const team of ["team1Result", "team2Result"]) {
            const teami = team === "team1Result" ? "team1" : "team2";
            for (const goalIndex in temp[team].goals) {
                let playerIndex = selectedMatch[teami].playerList.findIndex(
                    (player) =>
                        player._id ===
                        selectedMatchResult[team].goals[goalIndex].player._id
                );
                if (playerIndex === -1)
                    playerIndex =
                        selectedMatchResult[team].goals[goalIndex].player;
                _.update(
                    temp,
                    `${team}.goals[${goalIndex}].player`,
                    () => selectedMatch[teami].playerList[playerIndex]._id
                );

                let assistIndex = selectedMatch[teami].playerList.findIndex(
                    (player) =>
                        player._id ===
                        selectedMatchResult[team].goals[goalIndex].assist._id
                );
                if (assistIndex === -1)
                    assistIndex =
                        selectedMatchResult[team].goals[goalIndex].assist;
                _.update(
                    temp,
                    `${team}.goals[${goalIndex}].assist`,
                    () => selectedMatch[teami].playerList[assistIndex]._id
                );
            }
        }

        await dispatch(updateMatchResult(temp._id, temp));
        setShowMatchResult(false);
    };

    const onChangeGoalTeam1 = (e) => {
        selectedMatchResult.team1Result.totalGoals = e.target.value;
        const template = {
            name: "",
            player: "",
            assist: "",
            type: "",
            minute: "",
            second: "",
        };
        const tempArr = [];
        if (e.target.value && e.target.value > 0) {
            for (let index = 0; index < e.target.value; index++) {
                tempArr.push(template);
            }
        }
        selectedMatchResult.team1Result.goals = JSON.parse(
            JSON.stringify(tempArr)
        );
    };

    const onChangeGoalTeam2 = (e) => {
        selectedMatchResult.team2Result.totalGoals = e.target.value;
        const template = {
            name: "",
            player: "",
            assist: "",
            type: "",
            minute: "",
            second: "",
        };
        const tempArr = [];
        if (e.target.value && e.target.value > 0) {
            for (let index = 0; index < e.target.value; index++) {
                tempArr.push(template);
            }
        }
        selectedMatchResult.team2Result.goals = JSON.parse(
            JSON.stringify(tempArr)
        );
    };

    const handleChangeGoal = (e, team, index) => {
        var temp = _.cloneDeep(selectedMatchResult);
        _.set(
            temp,
            `[${team}].goals[${index}].[${e.target.name}]`,
            e.target.value
        );
        setSelectedMatchResult(temp);
    };

    const CalendarGenerate = () => {
        if (Object.keys(calendar).length === 0) {
            return <h1>Calendar Loading...</h1>;
        }
        if (
            calendar.calendar.awayMatches.length < 1 &&
            calendar.calendar.homeMatches.length < 1
        )
            return (
                <Button
                    variant="primary"
                    type="button"
                    onClick={() => dispatch(updateCalendar(calendar))}
                >
                    Tạo lịch thi đấu
                </Button>
            );
        return (
            <div>
                {["awayMatches", "homeMatches"].map((homeOrAway) => (
                    <>
                        {
                            <h3 className="text-center mt-5 bg-danger text-white">
                                {homeOrAway === "awayMatches"
                                    ? "Lượt đi"
                                    : "Lượt về"}
                            </h3>
                        }
                        {calendar.calendar[homeOrAway].map((round, index) => {
                            return (
                                <div key={index}>
                                    <h4 className="text-center mt-3">{`Round ${round.round}`}</h4>
                                    {index === 0 ? (
                                        <Row>
                                            <Col sm={1} className="text-center">
                                                <h5>Ngày</h5>
                                            </Col>
                                            <Col sm={1} className="text-center">
                                                <h5>Giờ</h5>
                                            </Col>
                                            <Col sm={4} className="text-center">
                                                <h5>Đội 1</h5>
                                            </Col>
                                            <Col sm={4} className="text-center">
                                                <h5>Đội 2</h5>
                                            </Col>
                                            <Col
                                                sm={2}
                                                className="text-center align-center"
                                            ></Col>
                                        </Row>
                                    ) : null}
                                    {round.matches.map((match) => {
                                        return (
                                            <MatchRow
                                                key={match._id}
                                                team1={match.team1.teamName}
                                                team2={match.team2.teamName}
                                                matchData={match}
                                                matchResult={match.result}
                                                setSelectedMatch={
                                                    setSelectedMatch
                                                }
                                                setShowMatchInfo={
                                                    setShowMatchInfo
                                                }
                                                setShowMatchResult={
                                                    setShowMatchResult
                                                }
                                                setSelectedMatchResult={
                                                    setSelectedMatchResult
                                                }
                                            />
                                        );
                                    })}
                                    {index !==
                                    calendar.calendar.awayMatches.length - 1 ? (
                                        <hr size={3} />
                                    ) : null}
                                </div>
                            );
                        })}
                    </>
                ))}
            </div>
        );
    };

    return (
        <Container className="mt-5">
            <CalendarGenerate />

            {/* MatchInfo Modal */}
            <Modal
                show={showMatchInfo}
                onHide={() => setShowMatchInfo(false)}
                className="w-100"
            >
                <Modal.Header closeButton>
                    <Modal.Title>Cập nhật thông tin trận đấu</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Row>
                            <Col>
                                <Form.Label>Ngày</Form.Label>
                                <Form.Control
                                    type="date"
                                    placeholder="Nhập tên đội"
                                    name="date"
                                    value={
                                        selectedMatch.date
                                            ? selectedMatch.date
                                            : ""
                                    }
                                    onChange={(e) =>
                                        setSelectedMatch({
                                            ...selectedMatch,
                                            date: e.target.value,
                                        })
                                    }
                                />
                            </Col>
                            <Col>
                                <Form.Label>Giờ</Form.Label>
                                <Form.Control
                                    type="time"
                                    placeholder="Nhập tên đội"
                                    value={
                                        selectedMatch.time
                                            ? selectedMatch.time
                                            : ""
                                    }
                                    name="time"
                                    onChange={(e) =>
                                        setSelectedMatch({
                                            ...selectedMatch,
                                            time: e.target.value,
                                        })
                                    }
                                />
                            </Col>
                        </Row>
                        <Row>
                            <Col>
                                <Form.Label>Đội 1</Form.Label>
                                <Form.Control
                                    type="text"
                                    placeholder="Đội 1"
                                    value={selectedMatch.team1.teamName}
                                    name="team1"
                                    onChange={(e) =>
                                        setSelectedMatch({
                                            ...selectedMatch,
                                            team1: e.target.value,
                                        })
                                    }
                                    disabled
                                />
                            </Col>
                            <Col>
                                <Form.Label>Đội 2</Form.Label>
                                <Form.Control
                                    type="text"
                                    placeholder="Đội 2"
                                    name="team2"
                                    value={selectedMatch.team2.teamName}
                                    onChange={(e) =>
                                        setSelectedMatch({
                                            ...selectedMatch,
                                            team2: e.target.value,
                                        })
                                    }
                                    disabled
                                />
                            </Col>
                        </Row>
                        <Row>
                            <Col>
                                <Form.Label>Sân</Form.Label>
                                <Form.Control
                                    type="text"
                                    placeholder="Nhập tên đội"
                                    value={selectedMatch.field}
                                    name="field"
                                    onChange={(e) =>
                                        setSelectedMatch({
                                            ...selectedMatch,
                                            field: e.target.value,
                                        })
                                    }
                                />
                            </Col>
                        </Row>
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={onSaveMatchInfo}>
                        Save
                    </Button>
                    <Button
                        variant="danger"
                        onClick={() => setShowMatchInfo(false)}
                    >
                        Cancel
                    </Button>
                </Modal.Footer>
            </Modal>

            {/* MatchResult Modal */}
            <Modal
                show={showMatchResult}
                onHide={() => setShowMatchResult(false)}
                size="lg"
                className="text-center"
            >
                <Modal.Header closeButton>
                    <Modal.Title>Cập nhật kết quả trận đấu</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Row>
                            <Form.Label>
                                <h5>Kết quả trận đấu</h5>
                            </Form.Label>
                            <Col className="text-center">
                                <Form.Label>
                                    {selectedMatch.team1.teamName}
                                </Form.Label>
                                <Form.Control
                                    type="number"
                                    name="result1"
                                    min={0}
                                    className="text-center"
                                    onChange={onChangeGoalTeam1}
                                    required
                                    disabled={
                                        selectedMatchResult.isConfirm ||
                                        selectedMatchResult.team1Result
                                            .totalGoals !== null
                                    }
                                    value={
                                        selectedMatchResult.team1Result
                                            .totalGoals
                                    }
                                />
                            </Col>
                            <Col className="text-center">
                                <Form.Label>
                                    {selectedMatch.team2.teamName}
                                </Form.Label>
                                <Form.Control
                                    type="number"
                                    name="result2"
                                    min={0}
                                    className="text-center"
                                    onChange={onChangeGoalTeam2}
                                    required
                                    disabled={
                                        selectedMatchResult.isConfirm ||
                                        selectedMatchResult.team2Result
                                            .totalGoals !== null
                                    }
                                    value={
                                        selectedMatchResult.team2Result
                                            .totalGoals
                                    }
                                />
                            </Col>
                        </Row>
                        <Modal.Footer className="border-top-0">
                            {selectedMatchResult.team1Result.totalGoals ===
                            null ? (
                                <Button
                                    variant="secondary"
                                    onClick={() => {
                                        if (
                                            selectedMatchResult.team1Result
                                                .totalGoals !== null &&
                                            selectedMatchResult.team2Result
                                                .totalGoals !== null
                                        )
                                            setSelectedMatchResult({
                                                ...selectedMatchResult,
                                                isConfirm: true,
                                            });
                                    }}
                                >
                                    Confirm Result
                                </Button>
                            ) : (
                                <></>
                            )}
                        </Modal.Footer>
                        {selectedMatchResult.isConfirm ? (
                            <Form.Label className="w-100">
                                <h5>Nhập thông tin các bàn thắng</h5>
                            </Form.Label>
                        ) : (
                            <></>
                        )}
                        {
                            <Row>
                                {["team1Result", "team2Result"].map((team) => (
                                    <Col key={team}>
                                        {selectedMatchResult[team].goals.map(
                                            (goal, index) => {
                                                const teami =
                                                    team === "team1Result"
                                                        ? "team1"
                                                        : "team2";
                                                let playerIndex = selectedMatch[
                                                    teami
                                                ].playerList.findIndex(
                                                    (player) =>
                                                        player._id ===
                                                        selectedMatchResult[
                                                            team
                                                        ].goals[index].player
                                                            ._id
                                                );
                                                if (playerIndex === -1)
                                                    playerIndex =
                                                        selectedMatchResult[
                                                            team
                                                        ].goals[index].player;
                                                let assistIndex = selectedMatch[
                                                    teami
                                                ].playerList.findIndex(
                                                    (player) =>
                                                        player._id ===
                                                        selectedMatchResult[
                                                            team
                                                        ].goals[index].assist
                                                            ._id
                                                );
                                                if (assistIndex === -1)
                                                    assistIndex =
                                                        selectedMatchResult[
                                                            team
                                                        ].goals[index].assist;
                                                return (
                                                    <Row
                                                        className="pt-2 text-left"
                                                        key={index}
                                                    >
                                                        <Col xs={1}>
                                                            <b>{index + 1}</b>
                                                        </Col>
                                                        <Col>
                                                            <Form.Select
                                                                name="player"
                                                                className="mt-1"
                                                                onChange={(e) =>
                                                                    handleChangeGoal(
                                                                        e,
                                                                        team,
                                                                        index
                                                                    )
                                                                }
                                                            >
                                                                <option>
                                                                    {!selectedMatchResult[
                                                                        team
                                                                    ].goals[
                                                                        index
                                                                    ].player
                                                                        ? "Cầu thủ ghi bàn"
                                                                        : selectedMatch[
                                                                              teami
                                                                          ]
                                                                              .playerList[
                                                                              playerIndex
                                                                          ]
                                                                              .playerName}
                                                                </option>
                                                                {selectedMatch[
                                                                    teami
                                                                ].playerList.map(
                                                                    (
                                                                        player,
                                                                        index
                                                                    ) => (
                                                                        <option
                                                                            value={
                                                                                index
                                                                            }
                                                                        >
                                                                            {
                                                                                player.playerName
                                                                            }
                                                                        </option>
                                                                    )
                                                                )}
                                                            </Form.Select>

                                                            <Form.Select
                                                                name="type"
                                                                className="mt-1"
                                                                onChange={(e) =>
                                                                    handleChangeGoal(
                                                                        e,
                                                                        team,
                                                                        index,
                                                                        teami
                                                                    )
                                                                }
                                                            >
                                                                <option>
                                                                    {!selectedMatchResult[
                                                                        team
                                                                    ].goals[
                                                                        index
                                                                    ].type
                                                                        ? "Loại bàn thắng"
                                                                        : selectedMatchResult[
                                                                              team
                                                                          ]
                                                                              .goals[
                                                                              index
                                                                          ]
                                                                              .type}
                                                                </option>
                                                                <option value="OG">
                                                                    OG
                                                                </option>
                                                                <option value="Penalty">
                                                                    Penalty
                                                                </option>
                                                                <option value="Normal">
                                                                    Normal
                                                                </option>
                                                            </Form.Select>

                                                            <Form.Select
                                                                name="assist"
                                                                className="mt-1"
                                                                onChange={(e) =>
                                                                    handleChangeGoal(
                                                                        e,
                                                                        team,
                                                                        index
                                                                    )
                                                                }
                                                            >
                                                                <option
                                                                    value={null}
                                                                >
                                                                    {!selectedMatchResult[
                                                                        team
                                                                    ].goals[
                                                                        index
                                                                    ].assist
                                                                        ? "Cầu thủ kiến tạo"
                                                                        : selectedMatch[
                                                                              teami
                                                                          ]
                                                                              .playerList[
                                                                              assistIndex
                                                                          ]
                                                                              .playerName}
                                                                </option>
                                                                {selectedMatch[
                                                                    teami
                                                                ].playerList.map(
                                                                    (
                                                                        player,
                                                                        index
                                                                    ) => (
                                                                        <option
                                                                            value={
                                                                                index
                                                                            }
                                                                        >
                                                                            {
                                                                                player.playerName
                                                                            }
                                                                        </option>
                                                                    )
                                                                )}
                                                            </Form.Select>

                                                            <Form.Control
                                                                className="mt-1"
                                                                type="number"
                                                                placeholder="Phút"
                                                                name="minute"
                                                                onChange={(e) =>
                                                                    handleChangeGoal(
                                                                        e,
                                                                        team,
                                                                        index
                                                                    )
                                                                }
                                                                value={
                                                                    selectedMatchResult[
                                                                        team
                                                                    ].goals[
                                                                        index
                                                                    ].minute
                                                                }
                                                            />

                                                            <Form.Control
                                                                className="mt-1"
                                                                type="number"
                                                                placeholder="Giây"
                                                                name="second"
                                                                onChange={(e) =>
                                                                    handleChangeGoal(
                                                                        e,
                                                                        team,
                                                                        index
                                                                    )
                                                                }
                                                                value={
                                                                    selectedMatchResult[
                                                                        team
                                                                    ].goals[
                                                                        index
                                                                    ].second
                                                                }
                                                            />
                                                        </Col>
                                                    </Row>
                                                );
                                            }
                                        )}
                                    </Col>
                                ))}
                            </Row>
                        }
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={onSaveMatchResult}>
                        Save
                    </Button>
                    <Button
                        variant="danger"
                        onClick={() => setShowMatchResult(false)}
                    >
                        Cancel
                    </Button>
                </Modal.Footer>
            </Modal>
        </Container>
    );
}

export default Calendar;
