import React, { useState } from "react";
import { Container } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import { Button, Form, Row, Col, Card } from "react-bootstrap";
import { createCalendar } from "../actions/calendar.js";

function HomeTour() {
    const calendar = useSelector((state) => state.calendar);
    const dispatch = useDispatch();

    const [calendarData, setCalendarData] = useState({
        Allteams: [],
        players: [],
        calendar: {
            awayMatches: [],
            homeMatches: [],
        },
        tourName: "",
        maxTeam: "",
        minTeam: "",
        maxPlayerOfTeam: "",
        minPlayerOfTeam: "",
        maxForeignPlayer: "",
        maxAge: "",
        minAge: "",
        ranking: [],
    });

    const handleSubmit = (e) => {
        e.preventDefault();

        dispatch(createCalendar(calendarData));
    };

    const handleChange = (e) => {
        setCalendarData({ ...calendarData, [e.target.name]: e.target.value });
    };

    if (Object.keys(calendar).length === 0) {
        return (
            <Container className="mt-5">
                <h3 className="text-center">Tạo giải đấu đầu tiên</h3>
                <Container fluid="sm">
                    <Form>
                        <Row className="mx-5 my-2">
                            <Col>
                                <Form.Label>Tên giải đấu</Form.Label>
                                <Form.Control
                                    type="text"
                                    name="tourName"
                                    onChange={handleChange}
                                    value={calendarData.tourName}
                                />
                            </Col>
                        </Row>
                        <Row className="mx-5 my-2">
                            <Col>
                                <Form.Label>Số đội tối thiểu</Form.Label>
                                <Form.Control
                                    type="number"
                                    name="minTeam"
                                    onChange={handleChange}
                                    value={calendarData.minTeam}
                                />
                            </Col>
                            <Col>
                                <Form.Label>Số đội tối đa</Form.Label>
                                <Form.Control
                                    type="number"
                                    name="maxTeam"
                                    onChange={handleChange}
                                    value={calendarData.maxTeam}
                                />
                            </Col>
                        </Row>
                        <Row className="mx-5 my-2">
                            <Col>
                                <Form.Label>
                                    Số cầu thủ tối thiểu mỗi đội
                                </Form.Label>
                                <Form.Control
                                    type="number"
                                    name="minPlayerOfTeam"
                                    onChange={handleChange}
                                    value={calendarData.minPlayerOfTeam}
                                />
                            </Col>
                            <Col>
                                <Form.Label>
                                    Số cầu thủ tối đa mỗi đội
                                </Form.Label>
                                <Form.Control
                                    type="number"
                                    name="maxPlayerOfTeam"
                                    onChange={handleChange}
                                    value={calendarData.maxPlayerOfTeam}
                                />
                            </Col>
                        </Row>
                        <Row className="mx-5 my-2">
                            <Col>
                                <Form.Label>Độ tuổi tối thiểu</Form.Label>
                                <Form.Control
                                    type="number"
                                    name="minAge"
                                    onChange={handleChange}
                                    value={calendarData.minAge}
                                />
                            </Col>
                            <Col>
                                <Form.Label>Độ tuổi tối đa</Form.Label>
                                <Form.Control
                                    type="number"
                                    name="maxAge"
                                    onChange={handleChange}
                                    value={calendarData.maxAge}
                                />
                            </Col>
                        </Row>
                        <Row className="mx-5 my-2">
                            <Col>
                                <Form.Label>
                                    Số cầu thủ nước ngoài tối đa
                                </Form.Label>
                                <Form.Control
                                    type="number"
                                    name="maxForeignPlayer"
                                    onChange={handleChange}
                                    value={calendarData.maxForeignPlayer}
                                />
                            </Col>
                        </Row>
                        <Row className="mx-5 my-2">
                            <Col sx={12}>
                                <Button
                                    className="text-right float-right my-2"
                                    type="button"
                                    onClick={handleSubmit}
                                >
                                    Tạo giải đấu
                                </Button>
                            </Col>
                        </Row>
                    </Form>
                </Container>
            </Container>
        );
    }
    return (
        <Container className="mt-5">
            <h3 className="text-center bg-danger text-white">
                Các giải đấu hiện có
            </h3>
            <Card>
                <Card.Header>{calendar.tourName}</Card.Header>
                <Card.Body></Card.Body>
                <Card.Footer>
                    <Button
                        
                    >
                        Đăng ký tham gia
                    </Button>
                </Card.Footer>
            </Card>
        </Container>
    );
}

export default HomeTour;
