import * as api from "../api/index.js";
import { FETCH_ALL_CALENDAR, UPDATE_CALENDAR, UPDATE_MATCH } from "../constants/actionTypes.js";

export const fetchCalendar = () => async (dispatch) => {
    try {
        const { data } = await api.fetchCalendar();
        
        dispatch({ type: FETCH_ALL_CALENDAR, payload: data });
    } catch (error) {
        console.log(error.response.data.message);
    }
};

export const updateCalendar = (calendar) => async (dispatch) => {
    try {
        const { data } = await api.updateCalendar(calendar);

        dispatch({ type: UPDATE_CALENDAR, payload: data });
    } catch (error) {
        console.log(error.response.data.message);
    }
};
