import React, { useState } from "react";
import { Container, Card, Button, Modal } from "react-bootstrap";
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";
import { acceptRegister, getRanking, getRankingPlayer } from "../../actions/tour";

const RegisterList = () => {
    const dispatch = useDispatch();
    const registerList = useSelector((state) => state.tour.registerList)
    const isAcceptingRegister = useSelector((state) => state.tour.isAcceptingRegister)

    const [selectedTeam, setSelectedTeam] = useState(null);

    const onAccepted = async (team) => {
        await dispatch(acceptRegister(team._id))
        await dispatch(getRanking());
        await dispatch(getRankingPlayer());
    }

    const onDelete = async (team, index) => {

    }

    const onDetail = async (team) => {
        setSelectedTeam(team)
    }
    
    return (
        <Container className="my-5">
            <h3 className="bg-danger text-center text-white">
                Danh sách đơn đăng ký
            </h3>
            {registerList.map((team, index) => (
                <Card className="my-2">
                <Card.Header as="h5"></Card.Header>
                <Card.Body>
                    <Card.Title>{team.teamName}</Card.Title>
                    <Card.Text>
                        Số lượng cầu thủ: {team.playerList.length}
                    </Card.Text>
                    <Button variant="primary" className="mx-2" onClick={() => onDetail(team)}>
                        Xem chi tiết
                    </Button>
                    <Button variant="primary" className="mx-2" onClick={() => onAccepted(team)}>
                        {isAcceptingRegister ? "Chấp nhận" : "Chấp nhận cập nhật team"}
                    </Button>
                    <Button variant="primary" className="mx-2" onClick={() => onDelete(team, index)}>
                        Xóa đơn
                    </Button>
                </Card.Body>
            </Card>
            ))}


        </Container>
    );
};

export default RegisterList;
