import React from "react";
import styled from "styled-components";

const Container = styled.div`
    position: fixed;
    width: 4%;
    height: 100vh;

    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
`;

const SidebarContainer = styled.div`
    position: relative;
    background-color: #d40f0f;
    width: 100%;
    height: 80%;
    color: #fff;

    display: flex;
    flex-direction: column;

    border-radius: 0 30px 30px 0;
`;

const ListItem = styled.ul`
    list-style: none;
    padding: 0;
    flex: 2;

    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: space-evenly;
`;

const Item = styled.li`
    font-size: 2rem;
    cursor: pointer;
`;

const ProfileContainer = styled.div`
    flex: 1;
    position: relative;

    display: flex;
    align-items: center;
    justify-content: center;
`;

const Profile = styled.img`
    width: 50px;
    height: 50px;

    border-radius: 50%;

    object-fit: cover;
    position: absolute;
    bottom: 0;
    margin-bottom: 50px;
`;

function Sidebar() {
    return (
        <Container>
            <SidebarContainer>
                <ListItem>
                    <Item title="abc">
                        <i className="fa-solid fa-house"></i>
                        <p>Home</p>
                    </Item>
                    <Item>
                        <i className="fa-solid fa-address-card"></i>
                        <p>Home</p>
                    </Item>
                    <Item>
                        <i className="fa-solid fa-arrows-turn-to-dots"></i>
                        <p>Home</p>
                    </Item>
                    <Item>
                        <i className="fa-brands fa-autoprefixer"></i>
                        <p>Home</p>
                    </Item>
                    <Item>
                        <i className="fa-solid fa-file-signature"></i>
                        <p>Home</p>
                    </Item>
                </ListItem>
                <ProfileContainer>
                    <Profile src="https://picsum.photos/300/300" alt="" />
                </ProfileContainer>
            </SidebarContainer>
        </Container>
    );
}

export default Sidebar;
