import {
    FETCH_ALL_CALENDAR,
    UPDATE_CALENDAR,
    UPDATE_MATCH,
    UPDATE_MATCH_RESULT,
    CREATE_CALENDAR
} from "../constants/actionTypes.js";

export default (calendar = {}, action) => {
    console.log(action.type);
    switch (action.type) {
        case CREATE_CALENDAR:
            return action.payload
        case FETCH_ALL_CALENDAR:
            return action.payload;
        case UPDATE_CALENDAR:
            return action.payload;
        case UPDATE_MATCH:
            if (calendar) {
                for (let homeOrAway of ["awayMatches", "homeMatches"]) {
                    for (const round in calendar.calendar[homeOrAway]) {
                        for (const match in calendar.calendar[homeOrAway][round]
                            .matches) {
                            if (
                                calendar.calendar[homeOrAway][round].matches[
                                    match
                                ]._id === action.payload._id
                            ) {
                                calendar.calendar[homeOrAway][round].matches[
                                    match
                                ] = action.payload;
                                console.log("Updated Data in reducer");
                                return calendar;
                            }
                        }
                    }
                }
            }
            return calendar;
        case UPDATE_MATCH_RESULT:
            if (Object.keys(calendar).length !== 0) {
                for (let homeOrAway of ["awayMatches", "homeMatches"]) {
                    for (const round in calendar.calendar[homeOrAway]) {
                        for (const match in calendar.calendar[homeOrAway][round]
                            .matches) {
                            if (
                                calendar.calendar[homeOrAway][round].matches[
                                    match
                                ].result._id === action.payload._id
                            ) {
                                calendar.calendar[homeOrAway][round].matches[
                                    match
                                ].result = action.payload;
                                console.log("Updated result in reducer");
                                return calendar;
                            }
                        }
                    }
                }
            }
            return calendar;
        default:
            return calendar;
    }
};
