import {
    UPDATE_MATCH
} from "../constants/actionTypes.js";

export default (matches = [], action) => {
    switch (action.type) {
        case UPDATE_MATCH:
            return matches.map((match) => {
                if (match._id === action.payload._id) return action.payload;
                else return match;
            });
        default:
            return calendar;
    }
};
