import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import { useEffect } from "react";
import { useDispatch } from "react-redux";

import "./App.css";
import Sidebar from "./components/Sidebar";
import CreateTeam from "./pages/createTeam";
import HomeTour from "./pages/homeTour";
import Calendar from "./pages/Calendar";
import HomePage from './pages/logIn/HomePage/HomePage.js'
import LoginForm from './pages/logIn/LoginPage/LoginForm'
import ForgottenPassword from './pages/logIn/LoginPage/ForgottenPassword'
import Register from './pages/logIn/LoginPage/Register'
import RegisterConfirm from './pages/logIn/LoginPage/RegisterConfirm'

import { GlobalStyle } from "./globalStyledComponent";
import "bootstrap/dist/css/bootstrap.min.css";
import { fetchTeams } from "./actions/teams.js";
import { fetchCalendar } from "./actions/calendar.js";

function App() {
    const dispatch = useDispatch();
    const isLogin = false

    useEffect(() => {
        dispatch(fetchTeams());
        dispatch(fetchCalendar());
    }, [dispatch]);

    return (
        <div>
            <GlobalStyle />
            <Router>
                { isLogin ?  <Sidebar /> : <></>}
                <Switch>
                    <Route exact path="/">
                        <HomePage />
                    </Route>
                    <Route exact path="/login">
                        <LoginForm />
                    </Route>
                    <Route exact path="/forgottenpassword">
                        <ForgottenPassword />
                    </Route>
                    <Route exact path="/register">
                        <Register />
                    </Route>
                    <Route exact path="/registerconfirmation">
                        <RegisterConfirm />
                    </Route>
                    <Route exact path="/tour">
                        <HomeTour />
                    </Route>
                    <Route exact path="/tour/newteam">
                        <CreateTeam />
                    </Route>
                    <Route exact path="/tour/calendar">
                        <Calendar />
                    </Route>
                </Switch>
            </Router>
        </div>
    );
}

export default App;
