import React, { useState } from "react";
import { Container, Button, Row, Col, Modal, Form } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import { updateCalendar } from "../actions/calendar.js";

function Calendar() {
    const dispatch = useDispatch();

    const [show, setShow] = useState(false);
    const [calendar] = useSelector((state) => state.calendar);

    const handleClose = () => {
        setShow(false);
    };
    const handleOpen = () => {
        setShow(true);
    };

    const updateMatch = () => {
        handleOpen();
    };

    const MatchRow = ({ team1, team2 }) => {
        return (
            <Row>
                <Col sm={1} className="text-center">
                    <p>Ngày</p>
                </Col>
                <Col sm={1} className="text-center">
                    <p>Giờ</p>
                </Col>
                <Col sm={4} className="text-center">
                    <p>{team1}</p>
                </Col>
                <Col sm={4} className="text-center">
                    <p>{team2}</p>
                </Col>
                <Col sm={2} className="text-center align-center">
                    <Button
                        variant="primary"
                        type="button"
                        onClick={updateMatch}
                    >
                        Cập nhật
                    </Button>
                </Col>
            </Row>
        );
    };

    const CalendarGenerate = () => {
        if (
            calendar.calendar.awayMatches.length < 1 &&
            calendar.calendar.homeMatches.length < 1
        ) {
            return (
                <Button
                    variant="primary"
                    type="button"
                    onClick={() => dispatch(updateCalendar(calendar))}
                >
                    Tạo lịch thi đấu
                </Button>
            );
        } else {
            return (
                <div>
                    <h3 className="text-center">Lượt đi</h3>
                    {calendar.calendar.awayMatches.map((round) => {
                        return (
                            <div>
                                <h5 className="text-center mt-3">{`Round ${round.round}`}</h5>
                                {round.matches.map((match) => {
                                    return (
                                        <MatchRow
                                            team1={match.team1.teamName}
                                            team2={match.team2.teamName}
                                        />
                                    );
                                })}
                            </div>
                        );
                    })}

                    <h3 className="text-center mt-5">Lượt về</h3>
                    {calendar.calendar.homeMatches.map((round) => {
                        return (
                            <div>
                                <h5 className="text-center mt-3">{`Round ${round.round}`}</h5>
                                {round.matches.map((match) => {
                                    return (
                                        <MatchRow
                                            team1={match.team1.teamName}
                                            team2={match.team2.teamName}
                                        />
                                    );
                                })}
                            </div>
                        );
                    })}
                </div>
            );
        }
    };

    return (
        <Container className="mt-5">
            <h1>{`Hiện tại đang có ${calendar.Allteams.length} đội bóng`}</h1>
            <CalendarGenerate />
            <Modal show={show} onHide={handleClose} className="w-100">
                <Modal.Header closeButton>
                    <Modal.Title>Cập nhật thông tin trận đấu</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Row>
                            <Col>
                                <Form.Label>Ngày</Form.Label>
                                <Form.Control
                                    type="date"
                                    placeholder="Nhập tên đội"
                                    // value={teamData.teamName}
                                    // onChange={(e) =>
                                    //     setTeamData({
                                    //         ...teamData,
                                    //         teamName: e.target.value,
                                    //     })
                                    // }
                                />
                            </Col>
                            <Col>
                                <Form.Label>Giờ</Form.Label>
                                <Form.Control
                                    type="time"
                                    placeholder="Nhập tên đội"
                                    // value={teamData.teamName}
                                    // onChange={(e) =>
                                    //     setTeamData({
                                    //         ...teamData,
                                    //         teamName: e.target.value,
                                    //     })
                                    // }
                                />
                            </Col>
                        </Row>
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Save
                    </Button>
                    <Button variant="danger" onClick={handleClose}>
                        Cancel
                    </Button>
                </Modal.Footer>
            </Modal>
        </Container>
    );
}

export default Calendar;
