import React, { useEffect, useState } from "react";
import { Container, Button, Row, Col, Modal, Form } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import { updateCalendar } from "../actions/calendar.js";
import MatchRow from "./MatchRow.js";

const initializeMatchData = {};

function Calendar() {
    const dispatch = useDispatch();
    const [calendar] = useSelector((state) => state.calendar);

    const [showMatchInfo, setShowMatchInfo] = useState();
    const [selectedMatch, setSelectedMatch] = useState();
    useEffect(() => {
        console.log(selectedMatch);
    }, [selectedMatch]);

    const CalendarGenerate = () => {
        if (
            calendar.calendar.awayMatches.length < 1 &&
            calendar.calendar.homeMatches.length < 1
        ) {
            return (
                <Button
                    variant="primary"
                    type="button"
                    onClick={() => dispatch(updateCalendar(calendar))}
                >
                    Tạo lịch thi đấu
                </Button>
            );
        } else {
            return (
                <div>
                    <h3 className="text-center bg-danger text-white">
                        Lượt đi
                    </h3>
                    {calendar.calendar.awayMatches.map((round, index) => {
                        return (
                            <div>
                                <h5 className="text-center mt-3">{`Round ${round.round}`}</h5>
                                {round.matches.map((match) => {
                                    return (
                                        <MatchRow
                                            key={match._id}
                                            team1={match.team1.teamName}
                                            team2={match.team2.teamName}
                                            matchData={match}
                                            setSelectedMatch={setSelectedMatch}
                                            setShowMatchInfo={setShowMatchInfo}
                                        />
                                    );
                                })}
                                {index !==
                                calendar.calendar.awayMatches.length - 1 ? (
                                    <hr size={3} />
                                ) : null}
                            </div>
                        );
                    })}

                    <h3 className="text-center mt-5 bg-danger text-white">
                        Lượt về
                    </h3>
                    {calendar.calendar.homeMatches.map((round, index) => {
                        return (
                            <div>
                                <h5 className="text-center mt-3">{`Round ${round.round}`}</h5>
                                {round.matches.map((match) => {
                                    return (
                                        <MatchRow
                                            team1={match.team1.teamName}
                                            team2={match.team2.teamName}
                                        />
                                    );
                                })}
                                {index !==
                                calendar.calendar.homeMatches.length - 1 ? (
                                    <hr size={3} />
                                ) : null}
                            </div>
                        );
                    })}
                </div>
            );
        }
    };

    return (
        <Container className="mt-5">
            <h1>{`Hiện tại đang có ${calendar.Allteams.length} đội bóng`}</h1>
            <CalendarGenerate />

            {/* MatchInfo Modal */}
            <Modal
                show={showMatchInfo}
                onHide={() => setShowMatchInfo(false)}
                className="w-100"
            >
                <Modal.Header closeButton>
                    <Modal.Title>Cập nhật thông tin trận đấu</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Row>
                            <Col>
                                <Form.Label>Ngày</Form.Label>
                                <Form.Control
                                    type="date"
                                    placeholder="Nhập tên đội"
                                    value={selectedMatch.date}
                                    // onChange={(e) =>
                                    //     setTeamData({
                                    //         ...teamData,
                                    //         teamName: e.target.value,
                                    //     })
                                    // }
                                />
                            </Col>
                            <Col>
                                <Form.Label>Giờ</Form.Label>
                                <Form.Control
                                    type="time"
                                    placeholder="Nhập tên đội"
                                    value={selectedMatch.time}
                                    // onChange={(e) =>
                                    //     setTeamData({
                                    //         ...teamData,
                                    //         teamName: e.target.value,
                                    //     })
                                    // }
                                />
                            </Col>
                        </Row>
                        <Row>
                            <Col>
                                <Form.Label>Sân</Form.Label>
                                <Form.Control
                                    type="text"
                                    placeholder="Nhập tên đội"
                                    value="Sân"
                                    // onChange={(e) =>
                                    //     setTeamData({
                                    //         ...teamData,
                                    //         teamName: e.target.value,
                                    //     })
                                    // }
                                />
                            </Col>
                        </Row>
                        <hr />
                        <Row>
                            <Col>
                                <Form.Label>Đội 1</Form.Label>
                                <Form.Control
                                    type="text"
                                    placeholder="Đội 1"
                                    // onChange={(e) =>
                                    //     setTeamData({
                                    //         ...teamData,
                                    //         teamName: e.target.value,
                                    //     })
                                    // }
                                />
                            </Col>
                            <Col>
                                <Form.Label>Đội 2</Form.Label>
                                <Form.Control
                                    type="text"
                                    placeholder="Đội 2"
                                    // onChange={(e) =>
                                    //     setTeamData({
                                    //         ...teamData,
                                    //         teamName: e.target.value,
                                    //     })
                                    // }
                                />
                            </Col>
                        </Row>
                        <Row>
                            <Col>
                                <Form.Label>Tỷ số</Form.Label>
                                <Form.Control
                                    type="text"
                                    placeholder="Tỷ số"
                                    // onChange={(e) =>
                                    //     setTeamData({
                                    //         ...teamData,
                                    //         teamName: e.target.value,
                                    //     })
                                    // }
                                />
                            </Col>
                        </Row>
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button
                        variant="secondary"
                        onClick={() => setShowMatchInfo(false)}
                    >
                        Save
                    </Button>
                    <Button
                        variant="danger"
                        onClick={() => setShowMatchInfo(false)}
                    >
                        Cancel
                    </Button>
                </Modal.Footer>
            </Modal>
        </Container>
    );
}

export default Calendar;
