import React, { useState } from "react";
import { Form, Button, Container, Row, Col } from "react-bootstrap";

function CreateTour() {
    const [teamData, setTeamData] = useState({
        teamName: "",
        homeGround: "",
        playerList: [
            {
                playerName: "",
                dayOfBirth: "",
                playerType: "",
                playerNote: "",
                numberOfGoals: 0,
                numberOfAssits: 0,
            },
        ],
    });

    const handleSubmit = (e) => {
        e.preventDefault();

        console.log(teamData);
    };

    const handlePlayerChange = (index, e) => {
        let data = [...teamData.playerList];

        if (e.target.name === "dayOfBirth")
            data[index][e.target.name] = Date(e.target.value);
        data[index][e.target.name] = e.target.value;

        setTeamData({ ...teamData, playerList: data });
    };

    const addPlayerData = () => {
        let newPlayerData = {
            playerName: "",
            dayOfBirth: "",
            playerType: "",
            playerNote: "",
            numberOfGoals: 0,
            numberOfAssits: 0,
        };

        setTeamData({ ...teamData, playerList: [...teamData.playerList, newPlayerData] });
    };

    const removePlayerData = (index) => {
        const data = [...teamData.playerList];
        data.splice(index, 1);

        setPlayerData(data);
        setTeamData({ ...teamData, playerList: playerData });
    };

    return (
        <Container style={{ padding: "50px 0px 0 0px" }}>
            <Form onSubmit={handleSubmit}>
                <Row>
                    <Col>
                        <Form.Group className="mb-3">
                            <Form.Label>Tên đội</Form.Label>
                            <Form.Control
                                type="text"
                                placeholder="Nhập tên đội"
                                value={teamData.teamName}
                                onChange={(e) =>
                                    setTeamData({
                                        ...teamData,
                                        teamName: e.target.value,
                                    })
                                }
                            />
                        </Form.Group>
                    </Col>
                    <Col>
                        <Form.Group className="mb-3">
                            <Form.Label>Sân nhà</Form.Label>
                            <Form.Control
                                type="text"
                                placeholder="Nhập tên sân nhà"
                                value={teamData.homeGround}
                                onChange={(e) =>
                                    setTeamData({
                                        ...teamData,
                                        homeGround: e.target.value,
                                    })
                                }
                            />
                        </Form.Group>
                    </Col>
                </Row>
                <Row>
                    <Col sm={1}>
                        <div style={{ textAlign: "center" }}>STT</div>
                    </Col>
                    <Col sm={3}>
                        <div style={{ textAlign: "center" }}>Tên cầu thủ</div>
                    </Col>
                    <Col sm={2}>
                        <div style={{ textAlign: "center" }}>Ngày sinh</div>
                    </Col>
                    <Col sm={2}>
                        <div style={{ textAlign: "center" }}>Loại cầu thủ</div>
                    </Col>
                    <Col sm={3}>
                        <div style={{ textAlign: "center" }}>Ghi chú</div>
                    </Col>
                    <Col sm={1}>
                        <div style={{ textAlign: "center" }}>Xóa cầu thủ</div>
                    </Col>
                </Row>

                {playerData.map((player, index) => {
                    return (
                        <Row key={index}>
                            <Col sm={1}>
                                <Form.Group
                                    className="mb-3"
                                    style={{ textAlign: "center" }}
                                >
                                    {index + 1}
                                </Form.Group>
                            </Col>
                            <Col sm={3}>
                                <Form.Group className="mb-3">
                                    <Form.Control
                                        type="text"
                                        value={player.playerName}
                                        name="playerName"
                                        onChange={(e) =>
                                            handlePlayerChange(index, e)
                                        }
                                    />
                                </Form.Group>
                            </Col>
                            <Col sm={2}>
                                <Form.Group className="mb-3">
                                    <Form.Control
                                        type="date"
                                        value={player.dayOfBirth}
                                        name="dayOfBirth"
                                        onChange={(e) =>
                                            handlePlayerChange(index, e)
                                        }
                                    />
                                </Form.Group>
                            </Col>
                            <Col sm={2}>
                                <Form.Group className="mb-3">
                                    <Form.Control
                                        type="text"
                                        value={player.playerType}
                                        name="playerType"
                                        onChange={(e) =>
                                            handlePlayerChange(index, e)
                                        }
                                    />
                                </Form.Group>
                            </Col>
                            <Col sm={3}>
                                <Form.Group className="mb-3">
                                    <Form.Control
                                        type="text"
                                        value={player.playerNote}
                                        name="playerNote"
                                        onChange={(e) =>
                                            handlePlayerChange(index, e)
                                        }
                                    />
                                </Form.Group>
                            </Col>
                            <Col
                                sm={1}
                                className="justify-content-center text-center"
                            >
                                <Button
                                    variant="primary"
                                    type="button"
                                    onClick={() => {
                                        removePlayerData(index);
                                    }}
                                >
                                    x
                                </Button>
                            </Col>
                        </Row>
                    );
                })}
                <div className="d-grid mb-3">
                    <Button
                        variant="primary"
                        type="button"
                        onClick={() => {
                            addPlayerData();
                        }}
                    >
                        +
                    </Button>
                </div>
                <div className="d-grid gap-2 mb-5">
                    <Button variant="danger" type="submit">
                        Submit
                    </Button>
                </div>
            </Form>
        </Container>
    );
}

export default CreateTour;
