export const signup = (userData) => async (dispatch) => {
    try {
        const { data } = await api.signup(userData)
        if (data.status !== 200)
        {
            throw data.message
        }
        dispatch({ type: SIGN_UP, payload: data })
        return { success: true, message: "Success"}
    } catch (error) {
        return { success: false, message: error}
    }
};
