import { CHECK_SIGN_IN, SIGN_IN, SIGN_UP } from "../constants/actionTypes.js";

const initializeUserData = {
    phoneNumber: "",
    country: "",
    userName: "",
    password: "",
    token: "",
    role: "",
    isLogin: false,
};

const user = (state = initializeUserData, action) => {
    switch (action.type) {
        case CHECK_SIGN_IN:
            return {...action.payload, isLogin: true};
        case SIGN_IN:
            return {...action.payload, isLogin: true};
        case SIGN_UP:
            return state;
        default:
            return state;
    }
};

export default user;
