import React from "react";
import { useEffect } from "react";
import { Container, Button } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import {updateCalendar} from '../actions/calendar.js';
import { fetchCalendar } from "../actions/calendar.js";

function Calendar() {
    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(fetchCalendar())
    }, [dispatch])

    const [calendar] = useSelector((state) => state.calendar);

    const calendarGenerate = () => {
        dispatch(updateCalendar(calendar))
    }

    return (
        <Container className="mt-5">
            <h1>{`Hiện tại đang có ${calendar.Allteams.length} đội bóng`}</h1>
            <Button variant="primary" type="button" onClick={() => calendarGenerate()}>
                Tạo lịch thi đấu
            </Button>
        </Container>
    );
}

export default Calendar;
