import React, { Component } from "react";
import { Button, Row, Col, Modal, Form } from "react-bootstrap";

export default class MatchRow extends Component {
    constructor(props) {
        super(props);
        this.state = { showMatchInfo: false };
    }

    onOpen = () => {
        this.setState({ showMatchInfo: true });
        console.log(this.state.showMatchInfo);
    };

    onClose = () => {
        this.setState({ showMatchInfo: false });
        console.log(this.state.showMatchInfo);
    };

    render() {
        return (
            <Row>
                <Col sm={1} className="text-center">
                    <p>Ngày</p>
                </Col>
                <Col sm={1} className="text-center">
                    <p>Giờ</p>
                </Col>
                <Col sm={4} className="text-center">
                    <p>{this.props.team1}</p>
                </Col>
                <Col sm={4} className="text-center">
                    <p>{this.props.team2}</p>
                </Col>
                <Col sm={2} className="text-center align-center">
                    <Button
                        variant="primary"
                        type="button"
                        onClick={

                            // console.log(this);
                            this.onShow
                        }
                    >
                        Cập nhật Thông tin
                    </Button>
                </Col>
            </Row>
        );
    }
}
