import React from "react";
import { Container, Button, Row } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import { useState } from "react";
import {updateCalendar} from "../actions/calendar.js";

function Calendar() {
    const dispatch = useDispatch();

    const calendar = useSelector((state) => state.calendar);
    console.log(calendar);
    const [showButton, setShowButton] = useState(
        calendar[0].calendar.awayMatches.length < 1 &&
            calendar[0].calendar.homeMatches.length < 1
            ? true
            : false
    );

    const calendarGenerate = () => {
        if (showButton) {
            return (
                <Button
                    variant="primary"
                    type="button"
                    onClick={() => setShowButton(false)}
                >
                    Tạo lịch thi đấu
                </Button>
            );
        } else {
            dispatch(updateCalendar(calendar[0]));

            return (
                <div>Hello</div>
            );
        }
    };

    return (
        <Container className="mt-5">
            <h1>{`Hiện tại đang có ${calendar[0].Allteams.length} đội bóng`}</h1>
            {calendarGenerate()}
        </Container>
    );
}

export default Calendar;
