import React, { useState } from "react";
import styled from "styled-components";

const Container = styled.div`
    position: fixed;
    width: ${(props) => props.showDetail};
    height: 100vh;

    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;

    transition: width 1s;
`;

const SidebarContainer = styled.div`
    position: relative;
    background-color: #d40f0f;
    width: 100%;
    height: 80%;
    color: #fff;

    display: flex;
    flex-direction: column;

    border-radius: 0 30px 30px 0;
`;

const ListItem = styled.ul`
    list-style: none;
    padding: 0;
    flex: 2;

    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: space-evenly;
`;

const Item = styled.li`
    font-size: 2rem;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: space-evenly;
    display: block;

    & i span {
        font-size: 1rem;
    }
`;

const ProfileContainer = styled.div`
    flex: 1;
    position: relative;

    display: flex;
    align-items: center;
    justify-content: center;
`;

const Profile = styled.img`
    width: 50px;
    height: 50px;

    border-radius: 50%;

    object-fit: cover;
    position: absolute;
    bottom: 0;
    margin-bottom: 50px;
`;

function Sidebar() {
    const [showDetail, setShowDetail] = useState("4%");

    return (
        <Container
            showDetail={showDetail}
            onMouseEnter={() => setShowDetail("10%")}
            onMouseLeave={() => setShowDetail("4%")}
        >
            <SidebarContainer>
                <ListItem>
                    <Item>
                        <i className="fa-solid fa-house"><span>Home</span></i>
                    </Item>
                    <Item>
                        <i className="fa-solid fa-address-card"><span>Home</span></i>
                    </Item>
                    <Item>
                        <i className="fa-solid fa-arrows-turn-to-dots"><span>Home</span></i>
                    </Item>
                    <Item>
                        <i className="fa-brands fa-autoprefixer"><span>Home</span></i>
                    </Item>
                    <Item>
                        <i className="fa-solid fa-file-signature"><span>Home</span></i>
                    </Item>
                </ListItem>
                <ProfileContainer>
                    <Profile src="https://picsum.photos/300/300" alt="" />
                </ProfileContainer>
            </SidebarContainer>
        </Container>
    );
}

export default Sidebar;
