import {
    FETCH_ALL_CALENDAR,
    UPDATE_CALENDAR,
    UPDATE_MATCH,
} from "../constants/actionTypes.js";

export default (calendar = [], action) => {
    switch (action.type) {
        case FETCH_ALL_CALENDAR:
            return action.payload;
        case UPDATE_CALENDAR:
            return action.payload;
        case UPDATE_MATCH:
            return matches.map((post) => {
                if (post._id === action.payload._id) return action.payload;
                else return post;
            });
        default:
            return calendar;
    }
};
