import React, { Component } from "react";
import { Button, Row, Col } from "react-bootstrap";

export default class MatchRow extends Component {
    state = { onChange: false }

    sendingStateToParent = () => {
        this.setState((prev) => ({ onChange: !prev.onChange }))
    }

    render() {
        return (
            <Row>
                <Col sm={1} className="text-center">
                    <p>{this.props.matchData.date}</p>
                </Col>
                <Col sm={1} className="text-center">
                    <p>{this.props.matchData.time}</p>
                </Col>
                <Col sm={4} className="text-center">
                    <p>{this.props.team1}</p>
                </Col>
                <Col sm={4} className="text-center">
                    <p>{this.props.team2}</p>   
                </Col>
                <Col sm={2} className="text-center align-center">
                    <Button
                        variant="primary"
                        type="button"
                        onClick={() => {
                            this.props.setShowMatchInfo(true)
                            this.props.setSelectedMatch(this.props.matchData)
                            this.props.setSelectedMatchState(this.sendingStateToParent)
                        }}
                    >
                        Cập nhật Thông tin
                    </Button>
                </Col>
            </Row>
        );
    }
}
