import React, { useState } from "react";
import { Container, Card, Button, Modal, Row, Col } from "react-bootstrap";
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";
import {
    acceptRegister,
    getRanking,
    getRankingPlayer,
} from "../../actions/tour";

const RegisterList = () => {
    const dispatch = useDispatch();
    const registerList = useSelector((state) => state.tour.registerList);
    const isAcceptingRegister = useSelector(
        (state) => state.tour.isAcceptingRegister
    );

    const [selectedTeam, setSelectedTeam] = useState(null);
    const [seeTeam, setSeeTeam] = useState(false);

    const onAccepted = async (team) => {
        await dispatch(acceptRegister(team._id));
        await dispatch(getRanking());
        await dispatch(getRankingPlayer());
    };

    const onDelete = async (team, index) => {};

    const onDetail = async (team) => {
        setSelectedTeam(team);
        setSeeTeam(true);
    };

    return (
        <Container className="my-5">
            <h3 className="bg-danger text-center text-white">
                Danh sách đơn đăng ký
            </h3>
            {registerList.map((team, index) => (
                <Card className="my-2">
                    <Card.Header as="h5"></Card.Header>
                    <Card.Body>
                        <Card.Title>{team.teamName}</Card.Title>
                        <Card.Text>
                            Số lượng cầu thủ: {team.playerList.length}
                        </Card.Text>
                        <Button
                            variant="primary"
                            className="mx-2"
                            onClick={() => onDetail(team)}
                        >
                            Xem chi tiết
                        </Button>
                        <Button
                            variant="primary"
                            className="mx-2"
                            onClick={() => onAccepted(team)}
                        >
                            {isAcceptingRegister
                                ? "Chấp nhận"
                                : "Chấp nhận cập nhật team"}
                        </Button>
                        <Button
                            variant="primary"
                            className="mx-2"
                            onClick={() => onDelete(team, index)}
                        >
                            Xóa đơn
                        </Button>
                    </Card.Body>
                </Card>
            ))}

            <Modal
                size="lg"
                show={seeTeam}
                onHide={() => setSeeTeam(false)}
                aria-labelledby="example-modal-sizes-title-lg"
            >
                <Modal.Header closeButton>
                    <Modal.Title id="example-modal-sizes-title-lg">
                        Thông tin đội
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Row>
                        <Col>{`Tên đội: ${selectedTeam?.teamName}`}</Col>
                        <Col>{`Sân nhà: ${selectedTeam?.homeGround}`}</Col>
                    </Row>
                    <Row className="text-center">
                        <Col>{`Tên cầu thủ`}</Col>
                        <Col>{`Quốc tịch`}</Col>
                        <Col>{`Vị trí`}</Col>
                        <Col>{`Số bàn thắng`}</Col>
                        <Col>{`Số kiến tạo`}</Col>
                    </Row>
                    {selectedTeam?.playerList.map((player) => (
                        <Row key={player?.playerName}>
                            <Col>{`Tên cầu thủ: ${player?.playerName}`}</Col>
                            <Col>{`Quốc tịch: ${player?.nationality}`}</Col>
                            <Col>{`Vị trí: ${player?.playerType}`}</Col>
                            <Col>{`Số bàn thắng: ${player?.allGoals.length}`}</Col>
                            <Col>{`Số kiến tạo: ${player?.allAssists.length}`}</Col>
                        </Row>
                    ))}
                </Modal.Body>
            </Modal>
        </Container>
    );
};

export default RegisterList;
