import React, {useState} from "react";
import { Container } from "react-bootstrap";
import { useSelector } from "react-redux";
import { Card, Button, Form, Row, Col } from "react-bootstrap";

function HomeTour() {
    const calendar = useSelector((state) => state.calendar);

    const [calendarData, setCalendarData] = useState({
        Allteams: [{ type: mongoose.Types.ObjectId, ref: "teamModel" }], // Tất cả các đội
        players: [{ type: mongoose.Types.ObjectId, ref: "playerModel" }], // Tất cả các cầu thủ
        calendar: {
            // Lịch thi đấu
            awayMatches: [
                // Lượt đi chứa danh sách các vòng thi đấu và các trận trong vòng thi đấu đó
                {
                    round: Number, // Vòng thi đấu
                    timeBegin: Date,
                    timeEnd: Date,
                    matches: [{ type: mongoose.Types.ObjectId, ref: "matchModel" }], // Các trận trong vòng thi đấu này
                },
            ],
            homeMatches: [
                // Lượt về chứa danh sách các vòng thi đấu và các trận trong vòng thi đấu đó
                {
                    round: Number, // Vòng thi đấu
                    timeBegin: Date,
                    timeEnd: Date,
                    matches: [{ type: mongoose.Types.ObjectId, ref: "matchModel" }], // Các trận trong vòng thi đấu này
                },
            ],
        },
        tourName: String,
        maxTeam: "",
        minTeam: "",
        maxPlayerOfTeam: "",
        minPlayerOfTeam: "",
        maxForeignPlayer: "",
        maxAge: "",
        minAge: "",
        ranking: []
    })

    const handleSubmit = (e) => {
        e.preventDefault();
    };

    if (Object.keys(calendar).length === 0) {
        return (
            <Container className="mt-5">
                <h3 className="text-center">Tạo giải đấu đầu tiên</h3>
                <Container fluid="sm">
                    <Form>
                        <Row className="mx-5 my-2">
                            <Col>
                                <Form.Label>Tên giải đấu</Form.Label>
                                <Form.Control type="text" />
                            </Col>
                        </Row>
                        <Row className="mx-5 my-2">
                            <Col>
                                <Form.Label>Số đội tối thiểu</Form.Label>
                                <Form.Control type="number" />
                            </Col>
                            <Col>
                                <Form.Label>Số đội tối đa</Form.Label>
                                <Form.Control type="number" />
                            </Col>
                        </Row>
                        <Row className="mx-5 my-2">
                            <Col>
                                <Form.Label>
                                    Số cầu thủ tối thiểu mỗi đội
                                </Form.Label>
                                <Form.Control type="number" />
                            </Col>
                            <Col>
                                <Form.Label>
                                    Số cầu thủ tối đa mỗi đội
                                </Form.Label>
                                <Form.Control type="number" />
                            </Col>
                        </Row>
                        <Row className="mx-5 my-2">
                            <Col>
                                <Form.Label>Độ tuổi tối thiểu</Form.Label>
                                <Form.Control type="number" />
                            </Col>
                            <Col>
                                <Form.Label>Độ tuổi tối đa</Form.Label>
                                <Form.Control type="number" />
                            </Col>
                        </Row>
                        <Row className="mx-5 my-2">
                            <Col>
                                <Form.Label>
                                    Số cầu thủ nước ngoài tối đa
                                </Form.Label>
                                <Form.Control type="number" />
                            </Col>
                        </Row>
                        <Row className="mx-5 my-2">
                            <Col sx={12}>
                                <Button
                                    className="text-right float-right my-2"
                                    type="button"
                                    onSubmit={handleSubmit}
                                >
                                    Tạo giải đấu
                                </Button>
                            </Col>
                        </Row>
                    </Form>
                </Container>
            </Container>
        );
    }
    return (
        <Container className="mt-5">
            {/* <h1>{`Giải này hiện có ${teams.length} đội bóng`}</h1>
            {teams.map((team) => {
                return (
                    <Card style={{ width: "100%" }}>
                        <Card.Body>
                            <Card.Title>{team.teamName}</Card.Title>
                            <Card.Text>
                                {team.homeGround}
                            </Card.Text>
                            <Button variant="primary">{`Đội này có ${team.playerList.length} cầu thủ`}</Button>
                        </Card.Body>
                    </Card>
                );
            })} */}
        </Container>
    );
}

export default HomeTour;
