import * as api from "../api/index.js";
import { SIGN_IN, SIGN_UP } from "../constants/actionTypes.js";

export const signin = (loginData, setUser) => async (dispatch) => {
    try {
        loginData.username = loginData.userName
        
        const {data} = await api.signin(loginData);
        setUser({...data, isLogin: true})
        dispatch({type: SIGN_IN, payload: data});
    } catch (error) {
        console.log(error.response.data.message);
    }
};

export const signup = (userData, setIsSuccess) => async (dispatch) => {
    try {
        const {data} = await api.signup(userData);
        setIsSuccess(true)
        dispatch({ type: SIGN_UP, payload: data });
    } catch (error) {
        alert(error.response.data.message)
    }
};
