import { FETCH_ALL, CREATE, ERROR } from "../constants/actionTypes.js";

export default (teams = [], action) => {
    switch (action.type) {
        case FETCH_ALL:
            return action.payload;
        case CREATE:
            return [...teams, action.payload];
        case ERROR:
