import React, { useState } from "react";
import { Container } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import { Button, Form, Row, Col, Card } from "react-bootstrap";
import { createTour } from "../../actions/tour.js";

function HomeTour() {
    const tour = useSelector((state) => state.tour);
    const dispatch = useDispatch();

    const [tourData, setTourData] = useState({
        allTeams: [],
        players: [],
        calendar: {
            awayMatches: [],
            homeMatches: [],
        },
        tourName: "",
        maxTeam: "",
        minTeam: "",
        maxPlayerOfTeam: "",
        minPlayerOfTeam: "",
        maxForeignPlayer: "",
        maxAge: "",
        minAge: "",
        ranking: [],
    });

    const handleSubmit = (e) => {
        e.preventDefault();

        dispatch(createTour(tourData));
    };

    const handleChange = (e) => {
        setTourData({ ...tourData, [e.target.name]: e.target.value });
    };

    if (Object.keys(tour).length === 0) {
        return (
            <Container className="mt-5">
                <h3 className="text-center">Tạo giải đấu đầu tiên</h3>
                <Container fluid="sm">
                    <Form>
                        <Row className="mx-5 my-2">
                            <Col>
                                <Form.Label>Tên giải đấu</Form.Label>
                                <Form.Control
                                    type="text"
                                    name="tourName"
                                    onChange={handleChange}
                                    value={tourData.tourName}
                                />
                            </Col>
                        </Row>
                        <Row className="mx-5 my-2">
                            <Col>
                                <Form.Label>Số đội tối thiểu</Form.Label>
                                <Form.Control
                                    type="number"
                                    name="minTeam"
                                    onChange={handleChange}
                                    value={tourData.minTeam}
                                />
                            </Col>
                            <Col>
                                <Form.Label>Số đội tối đa</Form.Label>
                                <Form.Control
                                    type="number"
                                    name="maxTeam"
                                    onChange={handleChange}
                                    value={tourData.maxTeam}
                                />
                            </Col>
                        </Row>
                        <Row className="mx-5 my-2">
                            <Col>
                                <Form.Label>
                                    Số cầu thủ tối thiểu mỗi đội
                                </Form.Label>
                                <Form.Control
                                    type="number"
                                    name="minPlayerOfTeam"
                                    onChange={handleChange}
                                    value={tourData.minPlayerOfTeam}
                                />
                            </Col>
                            <Col>
                                <Form.Label>
                                    Số cầu thủ tối đa mỗi đội
                                </Form.Label>
                                <Form.Control
                                    type="number"
                                    name="maxPlayerOfTeam"
                                    onChange={handleChange}
                                    value={tourData.maxPlayerOfTeam}
                                />
                            </Col>
                        </Row>
                        <Row className="mx-5 my-2">
                            <Col>
                                <Form.Label>Độ tuổi tối thiểu</Form.Label>
                                <Form.Control
                                    type="number"
                                    name="minAge"
                                    onChange={handleChange}
                                    value={tourData.minAge}
                                />
                            </Col>
                            <Col>
                                <Form.Label>Độ tuổi tối đa</Form.Label>
                                <Form.Control
                                    type="number"
                                    name="maxAge"
                                    onChange={handleChange}
                                    value={tourData.maxAge}
                                />
                            </Col>
                        </Row>
                        <Row className="mx-5 my-2">
                            <Col>
                                <Form.Label>
                                    Số cầu thủ nước ngoài tối đa
                                </Form.Label>
                                <Form.Control
                                    type="number"
                                    name="maxForeignPlayer"
                                    onChange={handleChange}
                                    value={tourData.maxForeignPlayer}
                                />
                            </Col>
                        </Row>
                        <Row className="mx-5 my-2">
                            <Col sx={12}>
                                <Button
                                    className="text-right float-right my-2"
                                    type="button"
                                    onClick={handleSubmit}
                                >
                                    Tạo giải đấu
                                </Button>
                            </Col>
                        </Row>
                    </Form>
                </Container>
            </Container>
        );
    }
    return (
        <Container className="mt-5">
            <h3 className="text-center bg-danger text-white">
                {tour.tourName}
            </h3>
            <h5>Các đội bóng hiện đang tham gia</h5>
            {tour.allTeams.map((team) => {
                console.log(team);
                return (
                    <Card key={team._id}>
                        <Card.Body>{team.teamName}</Card.Body>
                    </Card>
                );
            })}
            {!tour.isAcceptingRegister ? (
                <>
                    <h5 className="mt-5">Các trận đấu</h5>
                    {tour.calendar.awayMatches.map((round) =>
                        round.matches.map((match) => (
                            <Row className="text-center">
                                <Col>{match.team1.teamName}</Col>
                                <Col>vs</Col>
                                <Col>{match.team2.teamName}</Col>
                            </Row>
                        ))
                    )}

                    {tour.calendar.homeMatches.map((round) =>
                        round.matches.map((match) => (
                            <Row className="text-center">
                                <Col>{match.team1.teamName}</Col>
                                <Col>vs</Col>
                                <Col>{match.team2.teamName}</Col>
                            </Row>
                        ))
                    )}
                </>
            ) : (
                <></>
            )}
        </Container>
    );
}

export default HomeTour;
