import {
    FETCH_ALL_CALENDAR,
    UPDATE_CALENDAR,
    UPDATE_MATCH,
} from "../constants/actionTypes.js";

export default (calendar = {}, action) => {
    switch (action.type) {
        case FETCH_ALL_CALENDAR:
            return action.payload;
        case UPDATE_CALENDAR:
            return action.payload;
        case UPDATE_MATCH:
            console.log("redux update match");
            if (calendar) {
                for (let homeOrAway of ["awayMatches", "homeMatches"]) {
                    for (const round in calendar.calendar[homeOrAway]) {
                        for (const match in calendar.calendar[homeOrAway][round]
                            .matches) {
                            if (
                                calendar.calendar[homeOrAway][round].matches[
                                    match
                                ]._id === action.payload._id
                            ) {
                                calendar.calendar[homeOrAway][round].matches[
                                    match
                                ] = action.payload;
                                console.log("Updated Data in reducer");
                                return calendar;
                            }
                        }
                    }
                }
            }
            return calendar;
        default:
            return calendar;
    }
};
