import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import { useEffect, useState } from "react";
import { useDispatch } from "react-redux";

import "./App.css";
import Sidebar from "./components/Sidebar.js";
import CreateTeam from "./pages/createTeam/createTeam.js";
import HomeTour from "./pages/homeTour/homeTour.js";
import Calendar from "./pages/Calendar/Calendar.js";
import SearchPlayer from "./pages/searchPlayer/SearchPlayer";
import HomePage from "./pages/logIn/HomePage.js";

import { GlobalStyle } from "./globalStyledComponent";
import "bootstrap/dist/css/bootstrap.min.css";
import { fetchTour } from "./actions/tour.js";
import ProtectedRoute from "./components/ProtectedRoute";
import { useSelector } from "react-redux";

function App() {
    const dispatch = useDispatch();
    const user = useSelector((state) => state.user);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        const go = async () => {
            if (user.isLogin && !loading)
            {
                setLoading(!loading)
                await dispatch(fetchTour());
            }
        }
    }, [dispatch, user]);

    return (
        <div>
            <GlobalStyle />
            <Router>
                {user.isLogin ? <Sidebar /> : <></>}
                <Switch>
                    <Route exact path="/">
                        <HomePage />
                    </Route>
                    <ProtectedRoute exact isLogin={user.isLogin} path="/tour">
                        <HomeTour />
                    </ProtectedRoute>
                    <ProtectedRoute exact isLogin={user.isLogin} path="/tour/newteam">
                        <CreateTeam />
                    </ProtectedRoute>
                    <ProtectedRoute exact isLogin={user.isLogin} path="/tour/calendar">
                        <Calendar />
                    </ProtectedRoute>
                    <ProtectedRoute exact isLogin={user.isLogin} path="/tour/search">
                        <SearchPlayer />
                    </ProtectedRoute>
                </Switch>
            </Router>
        </div>
    );
}

export default App;
