import React, { useState } from "react";
import { Container, Button, Row, Col, Modal, Form } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import { updateCalendar } from "../actions/calendar.js";
import { updateMatch } from "../actions/match.js";
import MatchRow from "./MatchRow.js";
import './components/styleModal.css'

const initializeMatchData = {
    team1: "",
    team2: "",
    field: "",
    vongthidau: null,
    tenluotthidau: "",
    date: null,
    time: null,
    result: null,
    _id: null,
};
const initializeMatchResult = {
    team1Result: {
        totalGoals: null,
        goals: [
            {
                name: "",
                player: "",
                assist: "",
                type: "",
                time: null,
            },
        ],
    },
    team2Result: {
        totalGoals: null,
        goals: [
            {
                name: "",
                player: "",
                assist: "",
                type: "",
                time: null,
            },
        ],
    },
};

function Calendar() {
    const dispatch = useDispatch();
    const calendar = useSelector((state) => state.calendar);
    const [showMatchInfo, setShowMatchInfo] = useState(false);
    const [selectedMatch, setSelectedMatch] = useState(initializeMatchData);
    const [showMatchResult, setShowMatchResult] = useState(false);
    const [selectedMatchResult, setSelectedMatchResult] = useState(
        initializeMatchResult
    );

    const onSaveMatchInfo = async () => {
        await dispatch(updateMatch(selectedMatch._id, selectedMatch));
        setShowMatchInfo(false);
    };

    const onSaveMatchResult = async () => {
        setShowMatchResult(false);
    };

    const CalendarGenerate = () => {
        if (Object.keys(calendar).length === 0) {
            return <h1>Calendar Loading...</h1>;
        }
        if (
            calendar.calendar.awayMatches.length < 1 &&
            calendar.calendar.homeMatches.length < 1
        )
            return (
                <Button
                    variant="primary"
                    type="button"
                    onClick={() => dispatch(updateCalendar(calendar))}
                >
                    Tạo lịch thi đấu
                </Button>
            );
        return (
            <div>
                {["awayMatches", "homeMatches"].map((homeOrAway) => (
                    <>
                        {
                            <h3 className="text-center mt-5 bg-danger text-white">
                                {homeOrAway === "awayMatches"
                                    ? "Lượt đi"
                                    : "Lượt về"}
                            </h3>
                        }
                        {calendar.calendar[homeOrAway].map((round, index) => {
                            return (
                                <div key={index}>
                                    <h4 className="text-center mt-3">{`Round ${round.round}`}</h4>
                                    {index === 0 ? (
                                        <Row>
                                            <Col sm={1} className="text-center">
                                                <h5>Ngày</h5>
                                            </Col>
                                            <Col sm={1} className="text-center">
                                                <h5>Giờ</h5>
                                            </Col>
                                            <Col sm={4} className="text-center">
                                                <h5>Đội 1</h5>
                                            </Col>
                                            <Col sm={4} className="text-center">
                                                <h5>Đội 2</h5>
                                            </Col>
                                            <Col
                                                sm={2}
                                                className="text-center align-center"
                                            ></Col>
                                        </Row>
                                    ) : null}
                                    {round.matches.map((match) => {
                                        return (
                                            <MatchRow
                                                key={match._id}
                                                team1={match.team1.teamName}
                                                team2={match.team2.teamName}
                                                matchData={match}
                                                matchResult={match.result}
                                                setSelectedMatch={
                                                    setSelectedMatch
                                                }
                                                setShowMatchInfo={
                                                    setShowMatchInfo
                                                }
                                                setShowMatchResult={
                                                    setShowMatchResult
                                                }
                                                setSelectedMatchResult={
                                                    setSelectedMatchResult
                                                }
                                            />
                                        );
                                    })}
                                    {index !==
                                    calendar.calendar.awayMatches.length - 1 ? (
                                        <hr size={3} />
                                    ) : null}
                                </div>
                            );
                        })}
                    </>
                ))}
            </div>
        );
    };

    return (
        <Container className="mt-5">
            <CalendarGenerate />

            {/* MatchInfo Modal */}
            <Modal
                show={showMatchInfo}
                onHide={() => setShowMatchInfo(false)}
                className="w-100"
            >
                <Modal.Header closeButton>
                    <Modal.Title>Cập nhật thông tin trận đấu</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Row>
                            <Col>
                                <Form.Label>Ngày</Form.Label>
                                <Form.Control
                                    type="date"
                                    placeholder="Nhập tên đội"
                                    name="date"
                                    value={
                                        selectedMatch.date
                                            ? selectedMatch.date
                                            : ""
                                    }
                                    onChange={(e) =>
                                        setSelectedMatch({
                                            ...selectedMatch,
                                            date: e.target.value,
                                        })
                                    }
                                />
                            </Col>
                            <Col>
                                <Form.Label>Giờ</Form.Label>
                                <Form.Control
                                    type="time"
                                    placeholder="Nhập tên đội"
                                    value={
                                        selectedMatch.time
                                            ? selectedMatch.time
                                            : ""
                                    }
                                    name="time"
                                    onChange={(e) =>
                                        setSelectedMatch({
                                            ...selectedMatch,
                                            time: e.target.value,
                                        })
                                    }
                                />
                            </Col>
                        </Row>
                        <Row>
                            <Col>
                                <Form.Label>Đội 1</Form.Label>
                                <Form.Control
                                    type="text"
                                    placeholder="Đội 1"
                                    value={selectedMatch.team1.teamName}
                                    name="team1"
                                    onChange={(e) =>
                                        setSelectedMatch({
                                            ...selectedMatch,
                                            team1: e.target.value,
                                        })
                                    }
                                    disabled
                                />
                            </Col>
                            <Col>
                                <Form.Label>Đội 2</Form.Label>
                                <Form.Control
                                    type="text"
                                    placeholder="Đội 2"
                                    name="team2"
                                    value={selectedMatch.team2.teamName}
                                    onChange={(e) =>
                                        setSelectedMatch({
                                            ...selectedMatch,
                                            team2: e.target.value,
                                        })
                                    }
                                    disabled
                                />
                            </Col>
                        </Row>
                        <Row>
                            <Col>
                                <Form.Label>Sân</Form.Label>
                                <Form.Control
                                    type="text"
                                    placeholder="Nhập tên đội"
                                    value={selectedMatch.field}
                                    name="field"
                                    onChange={(e) =>
                                        setSelectedMatch({
                                            ...selectedMatch,
                                            field: e.target.value,
                                        })
                                    }
                                />
                            </Col>
                        </Row>
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={onSaveMatchInfo}>
                        Save
                    </Button>
                    <Button
                        variant="danger"
                        onClick={() => setShowMatchInfo(false)}
                    >
                        Cancel
                    </Button>
                </Modal.Footer>
            </Modal>

            {/* MatchInfo Modal */}
            <Modal
                show={showMatchResult}
                onHide={() => setShowMatchResult(false)}
                dialogClassName="modal-90w"
            >
                <Modal.Header closeButton>
                    <Modal.Title>Cập nhật kết quả trận đấu</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Row>
                            <Form.Label>Kết quả trận đấu</Form.Label>
                            <Col className="text-center">
                                <Form.Label>
                                    {selectedMatch.team1.teamName}
                                </Form.Label>
                                <Form.Control
                                    type="number"
                                    name="result1"
                                    min={0}
                                    className="text-center"
                                    onChange={(e) => {
                                        setSelectedMatch({
                                            ...selectedMatch,
                                            team1Result: {
                                                totalGoals: e.target.value,
                                                ...selectedMatch.team1Result,
                                            },
                                        });
                                    }}
                                    required
                                />
                            </Col>
                            <Col className="text-center">
                                <Form.Label>
                                    {selectedMatch.team2.teamName}
                                </Form.Label>
                                <Form.Control
                                    type="number"
                                    name="result2"
                                    min={0}
                                    className="text-center"
                                    onChange={(e) => {
                                        setSelectedMatch({
                                            ...selectedMatch,
                                            team2Result: {
                                                totalGoals: e.target.value,
                                                ...selectedMatch.team2Result,
                                            },
                                        });
                                    }}
                                    required
                                />
                            </Col>
                        </Row>
                        <Form.Label>Danh sách bàn thắng</Form.Label>
                        {selectedMatchResult.team1Result.totalGoals >=
                        selectedMatchResult.team2Result.totalGoals
                            ? selectedMatchResult.team1Result.goals.map(
                                  (goal, index) => {
                                      return (
                                          <Row>
                                              <Col>
                                                  <Row>
                                                      <Col xs={6}>
                                                          <Form.Control
                                                              type="text"
                                                              onChange={(e) => {
                                                                  setSelectedMatch(
                                                                      {
                                                                          ...selectedMatch,
                                                                          team2Result:
                                                                              {
                                                                                  totalGoals:
                                                                                      e
                                                                                          .target
                                                                                          .value,
                                                                                  ...selectedMatch.team2Result,
                                                                              },
                                                                      }
                                                                  );
                                                              }}
                                                          />
                                                      </Col>
                                                      <Col xs={2}>
                                                          <Form.Control
                                                              type="text"
                                                              onChange={(e) => {
                                                                  setSelectedMatch(
                                                                      {
                                                                          ...selectedMatch,
                                                                          team2Result:
                                                                              {
                                                                                  totalGoals:
                                                                                      e
                                                                                          .target
                                                                                          .value,
                                                                                  ...selectedMatch.team2Result,
                                                                              },
                                                                      }
                                                                  );
                                                              }}
                                                          />
                                                      </Col>
                                                      <Col xs={2}>
                                                          <Form.Control
                                                              type="number"
                                                              onChange={(e) => {
                                                                  setSelectedMatch(
                                                                      {
                                                                          ...selectedMatch,
                                                                          team2Result:
                                                                              {
                                                                                  totalGoals:
                                                                                      e
                                                                                          .target
                                                                                          .value,
                                                                                  ...selectedMatch.team2Result,
                                                                              },
                                                                      }
                                                                  );
                                                              }}
                                                          />
                                                      </Col>
                                                      <Col xs={2}>
                                                          <Form.Control
                                                              type="number"
                                                              onChange={(e) => {
                                                                  setSelectedMatch(
                                                                      {
                                                                          ...selectedMatch,
                                                                          team2Result:
                                                                              {
                                                                                  totalGoals:
                                                                                      e
                                                                                          .target
                                                                                          .value,
                                                                                  ...selectedMatch.team2Result,
                                                                              },
                                                                      }
                                                                  );
                                                              }}
                                                          />
                                                      </Col>
                                                  </Row>
                                              </Col>
                                              <Col>
                                                  <Form.Control
                                                      type="text"
                                                      onChange={(e) => {
                                                          setSelectedMatch({
                                                              ...selectedMatch,
                                                              team2Result: {
                                                                  totalGoals:
                                                                      e.target
                                                                          .value,
                                                                  ...selectedMatch.team2Result,
                                                              },
                                                          });
                                                      }}
                                                  />
                                              </Col>
                                          </Row>
                                      );
                                  }
                              )
                            : null}
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={onSaveMatchResult}>
                        Save
                    </Button>
                    <Button
                        variant="danger"
                        onClick={() => setShowMatchResult(false)}
                    >
                        Cancel
                    </Button>
                </Modal.Footer>
            </Modal>
        </Container>
    );
}

export default Calendar;
