import React, { useState } from "react";
import { Container, Button, Form, Row, Col } from "react-bootstrap";
import { useDispatch } from "react-redux";

function TourRule() {
    const tour = useDispatch((state) => state.tour);

    const initializeTourData = {
        tourName: tour.tourName,
        maxTeam: tour.maxTeam,
        minTeam: tour.minTeam,
        maxPlayerOfTeam: tour.maxPlayerOfTeam,
        minPlayerOfTeam: tour.minPlayerOfTeam,
        maxForeignPlayer: tour.maxForeignPlayer,
        maxAge: tour.maxAge,
        minAge: tour.minAge,
        isAcceptingRegister: tour.isAcceptingRegister,
        isClosed: tour.isClosed,
        dateStart: tour.dateStart,
        dateEnd: tour.dateEnd,
    };
    const [tourData, setTourData] = useState(initializeTourData);

    const handleChange = (e) => {
        setTourData({ ...tourData, [e.target.name]: e.target.value });
    };

    return (
        <Container className="mt-5">
            <h3 className="bg-danger text-center text-white">
                Quy định giải đấu
            </h3>
            <Form>
                <Form.Label>Tên giải đấu</Form.Label>
                <Form.Control
                    type="text"
                    value={tourData.tourName}
                    name="tourName"
                    onChange={handleChange}
                />
                <Row>
                    <Col>
                    <Form.Label>Số đội tối thiểu</Form.Label>
                <Form.Control
                    type="Number"
                    min={0}
                    value={tourData.minTeam}
                    name="minTeam"
                    onChange={handleChange}
                />
                    </Col>
                    <Col>
                    <Form.Label>Số đội tối đa</Form.Label>
                <Form.Control
                    type="text"
                    min={0}
                    value={tourData.maxTeam}
                    name="maxTeam"
                    onChange={handleChange}
                />
                    </Col>
                </Row>

                <Row>
                    <Col>
                    <Form.Label>Số cầu thủ tối thiểu</Form.Label>
                <Form.Control
                    type="number"
                    min={0}
                    value={tourData.minPlayerOfTeam}
                    name="minPlayerOfTeam"
                    onChange={handleChange}
                />
                    </Col>
                    <Col><Form.Label>Số cầu thủ tối đa</Form.Label>
                <Form.Control
                    type="number"
                    min={0}
                    value={tourData.maxPlayerOfTeam}
                    name="maxPlayerOfTeam"
                    onChange={handleChange}
                /></Col>
                </Row>

                <Row>
                <Form.Label>Số cầu thủ nước ngoài tối đa</Form.Label>
                <Form.Control
                    type="number"
                    min={0}
                    value={tourData.maxForeignPlayer}
                    name="maxForeignPlayer"
                    onChange={handleChange}
                />
                </Row>

                <Row>
                    <Col>
                    <Form.Label>Độ tuổi tối thiểu</Form.Label>
                <Form.Control
                    type="number"
                    min={0}
                    value={tourData.minPlayerOfTeam}
                    name="minPlayerOfTeam"
                    onChange={handleChange}
                />
                    </Col>
                    <Col><Form.Label>Số cầu thủ tối đa</Form.Label>
                <Form.Control
                    type="number"
                    min={0}
                    value={tourData.maxPlayerOfTeam}
                    name="maxPlayerOfTeam"
                    onChange={handleChange}
                /></Col>
                </Row>

                <Form.Label>Tên giải đấu</Form.Label>
                <Form.Control
                    type="text"
                    value={tourData.tourName}
                    name="tourName"
                    onChange={handleChange}
                />
                <Form.Label>Tên giải đấu</Form.Label>
                <Form.Control
                    type="text"
                    value={tourData.tourName}
                    name="tourName"
                    onChange={handleChange}
                />
                <Form.Label>Tên giải đấu</Form.Label>
                <Form.Control
                    type="text"
                    value={tourData.tourName}
                    name="tourName"
                    onChange={handleChange}
                />
                <Form.Label>Tên giải đấu</Form.Label>
                <Form.Control
                    type="text"
                    value={tourData.tourName}
                    name="tourName"
                    onChange={handleChange}
                />
                <Form.Label>Tên giải đấu</Form.Label>
                <Form.Control
                    type="text"
                    value={tourData.tourName}
                    name="tourName"
                    onChange={handleChange}
                />
                <Form.Label>Tên giải đấu</Form.Label>
                <Form.Control
                    type="text"
                    value={tourData.tourName}
                    name="tourName"
                    onChange={handleChange}
                />
                <Form.Label>Tên giải đấu</Form.Label>
                <Form.Control
                    type="text"
                    value={tourData.tourName}
                    name="tourName"
                    onChange={handleChange}
                />
                <Form.Label>Tên giải đấu</Form.Label>
                <Form.Control
                    type="text"
                    value={tourData.tourName}
                    name="tourName"
                    onChange={handleChange}
                />
                <Button type="submit">Save</Button>
            </Form>
        </Container>
    );
}

export default TourRule;
