import React from "react";
import { Container, Button, Row, Col } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import { updateCalendar } from "../actions/calendar.js";

function Calendar() {
    const dispatch = useDispatch();

    const [calendar] = useSelector((state) => state.calendar);
    console.log(calendar);
    const CalendarGenerate = () => {
        if (
            calendar.calendar.awayMatches.length < 1 &&
            calendar.calendar.homeMatches.length < 1
        ) {
            return (
                <Button
                    variant="primary"
                    type="button"
                    onClick={() => dispatch(updateCalendar(calendar))}
                >
                    Tạo lịch thi đấu
                </Button>
            );
        } else {
            return (
                <div>
                    <h3 className="text-center">Lượt đi</h3>
                    {calendar.calendar.awayMatches.map((round) => {
                        return (
                            <div>
                                <h5 className="text-center">{`Round ${round.round}`}</h5>
                                {round.matches.map((match) => {
                                    return (
                                        <Row>
                                            <Col className="text-center">
                                                <p>{match.team1.teamName}</p>
                                            </Col>
                                            <Col className="text-center">
                                                <p>{match.team2.teamName}</p>
                                            </Col>
                                        </Row>
                                    );
                                })}
                            </div>
                        );
                    })}

                    <h3 className="text-center">Lượt về</h3>
                    {calendar.calendar.homeMatches.map((round) => {
                        return (
                            <div>
                                <h5 className="text-center">{`Round ${round.round}`}</h5>
                                {round.matches.map((match) => {
                                    return (
                                        <Row>
                                            <Col className="text-center">
                                                <p>{match.team1.teamName}</p>
                                            </Col>
                                            <Col className="text-center">
                                                <p>{match.team2.teamName}</p>
                                            </Col>
                                        </Row>
                                    );
                                })}
                            </div>
                        );
                    })}
                </div>
            );
        }
    };

    return (
        <Container className="mt-5">
            <h1>{`Hiện tại đang có ${calendar.Allteams.length} đội bóng`}</h1>
            <CalendarGenerate />
        </Container>
    );
}

export default Calendar;
