import * as api from "../api/index.js";
import { FETCH_ALL_CALENDAR, CREATE_CALENDAR } from "../constants/actionTypes.js";

export const fetchCalendar = () => async (dispatch) => {
    try {
        const { data } = await api.fetchCalendar();

        dispatch({ type: FETCH_ALL_CALENDAR, payload: data });
    } catch (error) {
        console.log(error.message);
    }
};

export const createCalendar = () => async (dispatch) => {
    try {
        const {data} = await api.createCalendar();

        if (data.errors)
        {
            throw data.message
        }
        dispatch({ type: CREATE_CALENDAR, payload: data });
    } catch (error) {
        return error
        // console.log(error);
    }
};
