import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import { useEffect } from "react";
import { useDispatch } from "react-redux";

import "./App.css";
import Sidebar from "./components/Sidebar";
import CreateTeam from "./pages/createTeam";
import HomeTour from "./pages/homeTour";
import Calendar from "./pages/Calendar";
import HomePage from "./pages/logIn/HomePage/HomePage.js";

import { GlobalStyle } from "./globalStyledComponent";
import "bootstrap/dist/css/bootstrap.min.css";
import { fetchTeams } from "./actions/teams.js";
import { fetchCalendar } from "./actions/calendar.js";
import ProtectedRoute from "./components/ProtectedRoute";
import { useSelector } from "react-redux";

function App() {
    const dispatch = useDispatch();
    const user = useSelector((state) => state.user);

    useEffect(() => {
        dispatch(fetchTeams());
        dispatch(fetchCalendar());
    }, [dispatch]);

    return (
        <div>
            <GlobalStyle />
            <Router>
                {user.isLogin ? <Sidebar /> : <></>}
                <Switch>
                    <Route exact path="/">
                        <HomePage />
                    </Route>
                    <ProtectedRoute exact isLogin={user.isLogin} path="/tour">
                        <HomeTour />
                    </ProtectedRoute>
                    <ProtectedRoute exact isLogin={user.isLogin} path="/tour/newteam">
                        <CreateTeam />
                    </ProtectedRoute>
                    <ProtectedRoute exact isLogin={user.isLogin} path="/tour/calendar">
                        <Calendar />
                    </ProtectedRoute>
                </Switch>
            </Router>
        </div>
    );
}

export default App;
