import React from "react";
import {
    Container,
    Navbar,
    Row,
    Col,
    Button,
    Modal,
    Form,
} from "react-bootstrap";
import "./HomePage.css";
import { useState } from "react";
import { Link } from "react-router-dom";

const HomePage = () => {
    const [showLoginForm, setShowLoginForm] = useState(false);
    const [showRegisterForm, setShowRegisterForm] = useState(false);
    const [loginFormData, setLoginFormData] = useState({ phoneNumber: "", password: "" });
    const [registerFormData, setRegisterFormData] = useState();

    const ModalLogin = () => {
        return (
            <Modal show={showLoginForm} onHide={() => setShowLoginForm(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>Đăng nhập</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Row className="my-2">
                            <Col>
                                <Form.Label>Số điện thoại</Form.Label>
                                <Form.Control
                                    type="tel"
                                    placeholder="Nhập số điện thoại"
                                    name="phoneNumber"
                                    value={loginFormData.phoneNumber}
                                    onChange={(e) =>
                                        setLoginFormData({
                                            ...loginFormData,
                                            [e.target.name]: e.target.value,
                                        })
                                    }
                                />
                            </Col>
                        </Row>
                        <Row className="my-2">
                            <Col>
                                <Form.Label>Mật khẩu</Form.Label>
                                <Form.Control
                                    type="password"
                                    placeholder="Nhập mật khẩu"
                                    name="password"
                                    value={loginFormData.password}
                                    onChange={(e) =>
                                        {
                                            console.log([e.target.name]);
                                            setLoginFormData({
                                            ...loginFormData,
                                            [e.target.name]: e.target.value,
                                        })}
                                    }
                                />
                            </Col>
                        </Row>
                        <Row className="my-2">
                            <Col className="text-center">
                                <Button
                                    variant="danger"
                                    onClick={() => setShowLoginForm(true)}
                                    className="w-100"
                                >
                                    Đăng nhập
                                </Button>
                            </Col>
                        </Row>
                        <Row className="my-4 text-center">
                            <Link onClick={() => {}}>Quên mật khẩu</Link>
                        </Row>
                        <Row className="my-4">
                            <p className="my-1">
                                <small>Đăng nhập bằng</small>
                            </p>
                            <Col>
                                <Button variant="primary" className="w-100">
                                    Facebook
                                </Button>
                            </Col>
                            <Col>
                                <Button variant="danger" className="w-100">
                                    Google
                                </Button>
                            </Col>
                        </Row>
                        <Row className="my-4">
                            <p className="my-1">
                                <small>Chưa có tài khoản?</small>
                            </p>
                            <Col>
                                <Button
                                    variant="secondary"
                                    className="w-100"
                                    onClick={() => {
                                        setShowLoginForm(false);
                                        setShowRegisterForm(true);
                                    }}
                                >
                                    Đăng ký
                                </Button>
                            </Col>
                        </Row>
                    </Form>
                </Modal.Body>
            </Modal>
        );
    };

    const ModalRegister = () => {
        return (
            <Modal
                show={showRegisterForm}
                onHide={() => setShowRegisterForm(false)}
            >
                <Modal.Header closeButton>
                    <Modal.Title>Đăng ký</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Row className="my-2">
                            <Col>
                                <Form.Label>Số điện thoại</Form.Label>
                                <Form.Control
                                    type="tel"
                                    placeholder="Nhập số điện thoại"
                                    pattern="[0-9]{10}"
                                    // value={teamData.teamName}
                                    // onChange={(e) =>
                                    //     setTeamData({
                                    //         ...teamData,
                                    //         teamName: e.target.value,
                                    //     })
                                    // }
                                />
                            </Col>
                            <Col>
                                <Form.Label>Country</Form.Label>
                                <Form.Select aria-label="Default select example">
                                    <option>Select your country</option>
                                    <option value="1">One</option>
                                    <option value="2">Two</option>
                                    <option value="3">Three</option>
                                </Form.Select>
                            </Col>
                        </Row>
                        <Row className="my-2">
                            <Col>
                                <Form.Label>Tên tài khoản</Form.Label>
                                <Form.Control
                                    type="text"
                                    placeholder="Nhập tên tài khoản"
                                    // value={teamData.teamName}
                                    // onChange={(e) =>
                                    //     setTeamData({
                                    //         ...teamData,
                                    //         teamName: e.target.value,
                                    //     })
                                    // }
                                />
                            </Col>
                        </Row>
                        <Row className="my-2">
                            <Col>
                                <Form.Label>Mật khẩu</Form.Label>
                                <Form.Control
                                    type="text"
                                    placeholder="Nhập mật khẩu"
                                    // value={teamData.teamName}
                                    // onChange={(e) =>
                                    //     setTeamData({
                                    //         ...teamData,
                                    //         teamName: e.target.value,
                                    //     })
                                    // }
                                />
                            </Col>
                        </Row>
                        <Row className="my-2">
                            <Col>
                                <Form.Label>Xác nhận mật khẩu</Form.Label>
                                <Form.Control
                                    type="text"
                                    placeholder="Nhập lại mật khẩu"
                                    // value={teamData.teamName}
                                    // onChange={(e) =>
                                    //     setTeamData({
                                    //         ...teamData,
                                    //         teamName: e.target.value,
                                    //     })
                                    // }
                                />
                            </Col>
                        </Row>
                        <Row className="my-2">
                            <Col className="text-center">
                                <Button
                                    variant="danger"
                                    // onClick={() => {
                                    //     setShowRegisterForm(false);
                                    // }}
                                    className="w-100"
                                >
                                    Tiếp tục
                                </Button>
                            </Col>
                        </Row>
                        <Row className="my-4">
                            <p className="my-1">
                                <small>Đăng nhập bằng</small>
                            </p>
                            <Col>
                                <Button variant="primary" className="w-100">
                                    Facebook
                                </Button>
                            </Col>
                            <Col>
                                <Button variant="danger" className="w-100">
                                    Google
                                </Button>
                            </Col>
                        </Row>
                    </Form>
                </Modal.Body>
            </Modal>
        );
    };

    return (
        <Container fluid>
            <div className="home-page"></div>
            <Navbar>
                <Container fluid>
                    <Navbar.Brand href="#home" className="text-white">
                        Soccer Tour
                    </Navbar.Brand>
                    <Navbar.Toggle />
                    <Navbar.Collapse className="justify-content-end">
                        <Button
                            variant="danger"
                            className="mx-3"
                            onClick={() => setShowLoginForm(true)}
                        >
                            Đăng nhập
                        </Button>
                        <Button
                            variant="danger"
                            onClick={() => setShowRegisterForm(true)}
                        >
                            Đăng ký
                        </Button>
                    </Navbar.Collapse>
                </Container>
            </Navbar>
            <ModalLogin />
            <ModalRegister />
            <Container
                fluid
                className="text-center text-white"
                style={{
                    position: "relative",
                }}
            >
                <h1 className="my-5">Simple Approach to Manage Tournaments!</h1>
                <p className="my-1">
                    FootballTour.com to simplify tournament management process
                    in order to save your time
                </p>
                <p className="my-1">Join us now and you will love this.</p>
                <p className="my-5">
                    FootballTour tự tin đáp ứng được những nhu cầu thể thao mà
                    người quản lí đòi hỏi với chất lượng tuyệt vời nhất. Khán
                    giả yêu thích bóng đá hoàn toàn có thể thỏa mãn đam mê của
                    mình nhờ hệ thống tra cứu và quản lí bóng đá mà chúng tôi
                    cung cấp. Chắc chắn các bạn sẽ cảm thấy hài lòng với những
                    dịch vụ được cung cấp.
                </p>

                <Row>
                    <Col>
                        <h3>1.509</h3>
                        <p>GIẢI ĐẤU</p>
                    </Col>
                    <Col>
                        <h3>40.320</h3>
                        <p>ĐỘI</p>
                    </Col>
                    <Col>
                        <h3>1.052.202</h3>
                        <p>LƯỢT XEM</p>
                    </Col>
                </Row>
            </Container>
        </Container>
    );
};

export default HomePage;
