import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import { useEffect } from "react";
import { useDispatch } from "react-redux";

import "./App.css";
import Sidebar from "./components/Sidebar.js";
import CreateTeam from "./pages/createTeam/createTeam.js";
import HomeTour from "./pages/homeTour/homeTour.js";
import Calendar from "./pages/Calendar/Calendar.js";
import SearchPlayer from "./pages/searchPlayer/SearchPlayer";

import { GlobalStyle } from "./globalStyledComponent";
import "bootstrap/dist/css/bootstrap.min.css";
import { fetchTour } from "./actions/tour.js";
import ProtectedRoute from "./components/ProtectedRoute";
import { useSelector } from "react-redux";

function App() {
    const dispatch = useDispatch();
    const user = useSelector((state) => state.user);

    useEffect(() => {
        if (user.isLogin)
            dispatch(fetchTour());
    }, [dispatch, user]);

    return (
        <div>
            <GlobalStyle />
            <Router>
                {user.isLogin ? <Sidebar /> : <></>}
                <Switch>
                    <Route exact path="/">
                        <HomeTour />
                    </Route>
                    <ProtectedRoute exact isLogin={user.isLogin} path="/newteam">
                        <CreateTeam />
                    </ProtectedRoute>
                    <ProtectedRoute exact isLogin={user.isLogin} path="/calendar">
                        <Calendar />
                    </ProtectedRoute>
                    <ProtectedRoute exact isLogin={user.isLogin} path="/search">
                        <SearchPlayer />
                    </ProtectedRoute>
                </Switch>
            </Router>
        </div>
    );
}

export default App;
