import React, { useState } from "react";
import { Container } from "react-bootstrap";
import { useSelector } from "react-redux";
import { Card, Button, Form, Row, Col } from "react-bootstrap";

function HomeTour() {
    const calendar = useSelector((state) => state.calendar);

    const [calendarData, setCalendarData] = useState({
        Allteams: [],
        players: [],
        calendar: {
            awayMatches: [
                {
                    round: "",
                    timeBegin: "",
                    timeEnd: "",
                    matches: [],
                },
            ],
            homeMatches: [
                {
                    round: "",
                    timeBegin: "",
                    timeEnd: "",
                    matches: [],
                },
            ],
        },
        tourName: "",
        maxTeam: "",
        minTeam: "",
        maxPlayerOfTeam: "",
        minPlayerOfTeam: "",
        maxForeignPlayer: "",
        maxAge: "",
        minAge: "",
        ranking: [],
    });

    const handleSubmit = (e) => {
        e.preventDefault();
    };

    const handleChange = (e) => {
        setCalendarData({ ...calendarData, [e.target.name]: e.target.value });
    };

    if (Object.keys(calendar).length === 0) {
        return (
            <Container className="mt-5">
                <h3 className="text-center">Tạo giải đấu đầu tiên</h3>
                <Container fluid="sm">
                    <Form>
                        <Row className="mx-5 my-2">
                            <Col>
                                <Form.Label>Tên giải đấu</Form.Label>
                                <Form.Control type="text" name="tourName"  onChange={handleChange}/>
                            </Col>
                        </Row>
                        <Row className="mx-5 my-2">
                            <Col>
                                <Form.Label>Số đội tối thiểu</Form.Label>
                                <Form.Control type="number" name="minTeam"  onChange={handleChange}/>
                            </Col>
                            <Col>
                                <Form.Label>Số đội tối đa</Form.Label>
                                <Form.Control type="number" name="maxTeam"  onChange={handleChange}/>
                            </Col>
                        </Row>
                        <Row className="mx-5 my-2">
                            <Col>
                                <Form.Label>
                                    Số cầu thủ tối thiểu mỗi đội
                                </Form.Label>
                                <Form.Control
                                    type="number"
                                    name="minPlayerOfTeam" onChange={handleChange}
                                />
                            </Col>
                            <Col>
                                <Form.Label>
                                    Số cầu thủ tối đa mỗi đội
                                </Form.Label>
                                <Form.Control
                                    type="number"
                                    name="maxPlayerOfTeam" onChange={handleChange}
                                />
                            </Col>
                        </Row>
                        <Row className="mx-5 my-2">
                            <Col>
                                <Form.Label>Độ tuổi tối thiểu</Form.Label>
                                <Form.Control type="number" name="minAge"  onChange={handleChange}/>
                            </Col>
                            <Col>
                                <Form.Label>Độ tuổi tối đa</Form.Label>
                                <Form.Control type="number" name="maxAge"  onChange={handleChange}/>
                            </Col>
                        </Row>
                        <Row className="mx-5 my-2">
                            <Col>
                                <Form.Label>
                                    Số cầu thủ nước ngoài tối đa
                                </Form.Label>
                                <Form.Control
                                    type="number"
                                    name="maxForeignPlayer" onChange={handleChange}
                                />
                            </Col>
                        </Row>
                        <Row className="mx-5 my-2">
                            <Col sx={12}>
                                <Button
                                    className="text-right float-right my-2"
                                    type="button"
                                    onSubmit={handleSubmit}
                                >
                                    Tạo giải đấu
                                </Button>
                            </Col>
                        </Row>
                    </Form>
                </Container>
            </Container>
        );
    }
    return (
        <Container className="mt-5">
            {/* <h1>{`Giải này hiện có ${teams.length} đội bóng`}</h1>
            {teams.map((team) => {
                return (
                    <Card style={{ width: "100%" }}>
                        <Card.Body>
                            <Card.Title>{team.teamName}</Card.Title>
                            <Card.Text>
                                {team.homeGround}
                            </Card.Text>
                            <Button variant="primary">{`Đội này có ${team.playerList.length} cầu thủ`}</Button>
                        </Card.Body>
                    </Card>
                );
            })} */}
        </Container>
    );
}

export default HomeTour;
