import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { Container, Row, Col, Form } from "react-bootstrap";
import { fetchPlayers } from '../../actions/player.js'

function SearchPlayer() {
    const dispatch = useDispatch();
    const calendar = useSelector((state) => state.calendar);

    useEffect(() => {
        if (calendar && calendar.players.length > 0 && Object.keys(calendar.players[0]).length === 0)
            dispatch(fetchPlayers(calendar.players))
        console.log("Loading players data");
    }, [calendar])
    const players = useSelector((state) => state.players)
    console.log(players);

    const [searchTerm, setSearchTerm] = useState({
        playerName: "",
        team: "",
        playerType: "",
        nationality: "",
    });

    const handleChange = (e) => {
        setSearchTerm({ ...searchTerm, [e.target.name]: e.target.value });
    };

    if (Object.keys(calendar).length === 0) {
        return (
            <Container className="mt-5">
                <h1>Loading data...</h1>
            </Container>
        );
    }
    return (
        <Container className="mt-5">
            <h3 className="text-center bg-danger text-white">
                Tra cứu cầu thủ
            </h3>
            <Form className="text-center">
                <Row>
                    <Col>
                        <Form.Label>
                            <b>Tên cầu thủ</b>
                        </Form.Label>
                        <Form.Control
                            type="text"
                            onChange={handleChange}
                            name="playerName"
                            value={searchTerm.playerName}
                        ></Form.Control>
                    </Col>
                    <Col>
                        <Form.Label>
                            <b>Đội</b>
                        </Form.Label>
                        <Form.Control
                            type="text"
                            onChange={handleChange}
                            name="team"
                            value={searchTerm.team}
                        ></Form.Control>
                    </Col>
                    <Col>
                        <Form.Label>
                            <b>Loại cầu thủ</b>
                        </Form.Label>
                        <Form.Control
                            type="text"
                            onChange={handleChange}
                            name="playerType"
                            value={searchTerm.playerType}
                        ></Form.Control>
                    </Col>
                    <Col>
                        <Form.Label>
                            <b>Quốc tịch</b>
                        </Form.Label>
                        <Form.Control
                            type="text"
                            onChange={handleChange}
                            name="nationality"
                            value={searchTerm.nationality}
                        ></Form.Control>
                    </Col>
                </Row>
            </Form>
            
            {/* Display player */}
            { players.map((player) => {

            }) }
        </Container>
    );
}

export default SearchPlayer;
