import {
    FETCH_ALL_CALENDAR,
    UPDATE_CALENDAR,
    UPDATE_MATCH
} from "../constants/actionTypes.js";

export default (calendar = {}, action) => {
    switch (action.type) {
        case FETCH_ALL_CALENDAR:
            return action.payload;
        case UPDATE_CALENDAR:
            return action.payload;
        case UPDATE_MATCH:
            if (calendar)
            {
                for (let homeOrAway of ["awayMatches", "homeMatches"]) {
                    for (const round in calendar.calendar[homeOrAway]) {
                        for (const match in data.calendar[homeOrAway][round].matches) {
                            if (data.calendar[homeOrAway][round].matches[match]._id == action.payload._id)
                            {
                                data.calendar[homeOrAway][round].matches[match] = action.payload;
                            }
                        }
                    }
                }
            }
        default:
            return calendar;
    }
};
